import numpy as np
def pegasos_kernel_svm(data: np.ndarray, labels: np.ndarray, kernel='linear', lambda_val=0.01, iterations=100, sigma=1.0):
    """
    Implements the deterministic Pegasos algorithm for kernel SVM.

    Parameters:
    - data: 2D NumPy array where each row is a sample and each column is a feature.
    - labels: 1D NumPy array of binary labels (-1 or 1).
    - kernel: Type of kernel ('linear' or 'RBF').
    - lambda_val: Regularization parameter.
    - iterations: Number of training iterations.
    - sigma: Sigma parameter for the RBF kernel.

    Returns:
    - alpha: List of alpha coefficients.
    - bias: Bias term.
    """
    n_samples = data.shape[0]
    alpha = np.zeros(n_samples)
    bias = 0.0
    if kernel == 'linear':

        def kernel_func(x, y):
            return np.dot(x, y)
    elif kernel == 'RBF':

        def kernel_func(x, y):
            return np.exp(-np.linalg.norm(x - y) ** 2 / (2 * sigma ** 2))
    else:
        raise ValueError("Kernel not supported. Choose 'linear' or 'RBF'.")
    K = np.zeros((n_samples, n_samples))
    for i in range(n_samples):
        for j in range(n_samples):
            K[i, j] = kernel_func(data[i], data[j])
    for _ in range(iterations):
        eta = 1.0 / (lambda_val * (_ + 1))
        predictions = np.zeros(n_samples)
        for i in range(n_samples):
            predictions[i] = np.sum(alpha * labels * K[:, i]) + bias
        for i in range(n_samples):
            if labels[i] * predictions[i] < 1:
                alpha[i] += eta * labels[i]
        alpha = np.maximum(alpha, 0)
        bias = np.mean(labels - np.dot(K, alpha * labels))
    alpha_rounded = np.round(alpha, 4).tolist()
    bias_rounded = np.round(bias, 4).tolist()
    return (alpha_rounded, bias_rounded)
