from itertools import combinations_with_replacement
def polynomial_features(X, degree):
    if degree < 1:
        raise ValueError('Degree must be at least 1')
    (n_samples, n_features) = X.shape
    features = []
    for d in range(1, degree + 1):
        for comb in combinations_with_replacement(range(n_features), d):
            exponents = np.zeros(n_features, dtype=int)
            for idx in comb:
                exponents[idx] += 1
            features.append(exponents)
    unique_features = []
    seen = set()
    for feat in features:
        tuple_feat = tuple(feat)
        if tuple_feat not in seen:
            seen.add(tuple_feat)
            unique_features.append(feat)
    unique_features.sort(key=lambda x: (sum(x), tuple(x)))
    X_poly = np.ones((n_samples, len(unique_features)))
    for (i, exponents) in enumerate(unique_features):
        X_poly[:, i] = np.prod(np.power(X, exponents), axis=1)
    return X_poly.tolist()
