def rref(matrix):
    mat = np.array(matrix, dtype=np.float64)
    (rows, cols) = mat.shape
    lead = 0
    for r in range(rows):
        if lead >= cols:
            break
        i = r
        while mat[i, lead] == 0:
            i += 1
            if i == rows:
                i = r
                lead += 1
                if cols == lead:
                    return mat.tolist()
        mat[[i, r]] = mat[[r, i]]
        pivot = mat[r, lead]
        if pivot != 0:
            mat[r] = mat[r] / pivot
        for i in range(rows):
            if i != r and mat[i, lead] != 0:
                mat[i] = mat[i] - mat[i, lead] * mat[r]
        lead += 1
    return mat.tolist()
