def compute_tf_idf(corpus, query):
    if not corpus:
        return []
    num_documents = len(corpus)
    query_terms = query
    tf_idf_scores = []
    df = {}
    for term in query_terms:
        df[term] = 0
        for doc in corpus:
            if term in doc:
                df[term] += 1
    idf = {}
    for term in query_terms:
        idf_value = math.log((num_documents + 1) / (df[term] + 1)) + 1
        idf[term] = idf_value
    for doc in corpus:
        doc_tf_idf = []
        total_terms_in_doc = len(doc)
        for term in query_terms:
            if total_terms_in_doc == 0:
                tf = 0.0
            else:
                tf = doc.count(term) / total_terms_in_doc
            tf_idf = tf * idf[term]
            doc_tf_idf.append(round(tf_idf, 5))
        tf_idf_scores.append(doc_tf_idf)
    return tf_idf_scores
