import numpy as np

def load_encoder_hparams_and_params(model_size: str = "124M", models_dir: str = "models"):
    class DummyBPE:
        def __init__(self):
            self.encoder_dict = {"hello": 1, "world": 2, "<UNK>": 0}

        def encode(self, text: str):
            tokens = text.strip().split()
            return [self.encoder_dict.get(token, self.encoder_dict["<UNK>"]) for token in tokens]

        def decode(self, token_ids: list):
            reversed_dict = {v: k for k, v in self.encoder_dict.items()}
            return " ".join([reversed_dict.get(tok_id, "<UNK>") for tok_id in token_ids])

    hparams = {
        "n_ctx": 1024,
        "n_head": 12
    }

    params = {
        "wte": np.random.rand(3, 10),
        "wpe": np.random.rand(1024, 10),
        "blocks": [],
        "ln_f": {
            "g": np.ones(10),
            "b": np.zeros(10),
        }
    }

    encoder = DummyBPE()
    return encoder, hparams, params
def feed_forward(x, ff_params):
    return np.matmul(np.maximum(0, np.matmul(x, ff_params['w1']) + ff_params['b1']), ff_params['w2']) + ff_params['b2']
def transformer_block(x, block_params, n_head):
    attn_out = multi_head_attention(layer_norm(x, block_params['ln_1']['g'], block_params['ln_1']['b']), n_head, block_params['attn'])
    x = x + attn_out
    ff_out = feed_forward(layer_norm(x, block_params['ln_2']['g'], block_params['ln_2']['b']), block_params['mlp'])
    x = x + ff_out
    return x
def gen_text(prompt: str, n_tokens_to_generate: int=40):
    np.random.seed(42)
    (encoder, hparams, params) = load_encoder_hparams_and_params()
    if not params['blocks']:
        d_model = params['wte'].shape[1]
        for _ in range(12):
            block_params = {'ln_1': {'g': np.ones(d_model), 'b': np.zeros(d_model)}, 'attn': {'q_proj': np.random.randn(d_model, d_model) * 0.02, 'k_proj': np.random.randn(d_model, d_model) * 0.02, 'v_proj': np.random.randn(d_model, d_model) * 0.02, 'out_proj': np.random.randn(d_model, d_model) * 0.02}, 'ln_2': {'g': np.ones(d_model), 'b': np.zeros(d_model)}, 'mlp': {'w1': np.random.randn(d_model, 4 * d_model) * 0.02, 'b1': np.zeros(4 * d_model), 'w2': np.random.randn(4 * d_model, d_model) * 0.02, 'b2': np.zeros(d_model)}}
            params['blocks'].append(block_params)
    input_ids = encoder.encode(prompt)
    generated_ids = list(input_ids)
    for _ in range(n_tokens_to_generate):
        x = params['wte'][input_ids] + params['wpe'][range(len(input_ids))]
        for block in params['blocks']:
            x = transformer_block(x, block, hparams['n_head'])
        x = layer_norm(x, params['ln_f']['g'], params['ln_f']['b'])
        logits = np.matmul(x[-1], params['wte'].T)
        probs = softmax(logits)
        next_token = np.random.choice(len(probs), p=probs)
        input_ids = [next_token]
        generated_ids.append(next_token)
    return encoder.decode(generated_ids)
assert gen_text("hello", 5) == "hello hello hello <UNK> <UNK>"
assert gen_text("hello world", 10) == "world world world world world world world world world world"
assert gen_text("world", 3) == "world world world"

