def pattern_weaver(n, crystal_values, dimension):

    def softmax(values):
        exp_values = np.exp(values - np.max(values))
        return exp_values / np.sum(exp_values)
    attention_scores = np.zeros((n, n))
    for i in range(n):
        for j in range(n):
            attention_scores[i, j] = crystal_values[i] * crystal_values[j]
    softmax_scores = np.zeros((n, n))
    for i in range(n):
        softmax_scores[i] = softmax(attention_scores[i])
    enhanced_patterns = np.zeros(n)
    for i in range(n):
        weighted_sum = 0.0
        for j in range(n):
            weighted_sum += softmax_scores[i, j] * crystal_values[j]
        enhanced_patterns[i] = weighted_sum
    enhanced_patterns_rounded = [round(val, 4) for val in enhanced_patterns]
    return enhanced_patterns_rounded
