def softplus(x: float) -> float:
    """
    Compute the softplus activation function.

    Args:
        x: Input value

    Returns:
        The softplus value: log(1 + e^x), rounded to the 4th decimal place.
    """
    if x > 30:
        return round(x, 4)
    elif x < -30:
        return round(math.exp(x), 4)
    else:
        return round(math.log(1 + math.exp(x)), 4)
