from typing import List, Dict, Tuple, Hashable
def find_frequent_itemsets(transactions: List[List[Hashable]], minsup: float) -> Dict[Tuple[Hashable, ...], int]:
    """Return all frequent itemsets in **transactions**."""
    if not transactions or minsup <= 0:
        return {}
    N = len(transactions)
    min_support_count = math.ceil(minsup * N)
    if min_support_count < 1:
        min_support_count = 1
    transaction_sets = [set(t) for t in transactions]
    item_counter = defaultdict(int)
    for t in transaction_sets:
        for item in t:
            item_counter[item] += 1
    frequent_items = {item for (item, count) in item_counter.items() if count >= min_support_count}
    frequent_itemsets = {}
    previous_level = [frozenset([item]) for item in sorted(frequent_items)]
    for itemset in previous_level:
        itemset_tuple = tuple(sorted(itemset))
        frequent_itemsets[itemset_tuple] = item_counter[next(iter(itemset))]
    k = 2
    while previous_level:
        current_level = []
        candidates = set()
        for i in range(len(previous_level)):
            for j in range(i + 1, len(previous_level)):
                itemset1 = previous_level[i]
                itemset2 = previous_level[j]
                new_candidate = itemset1.union(itemset2)
                if len(new_candidate) == k:
                    candidates.add(new_candidate)
        candidates = list(candidates)
        candidate_support = defaultdict(int)
        for candidate in candidates:
            candidate_tuple = tuple(sorted(candidate))
            for t in transaction_sets:
                if candidate.issubset(t):
                    candidate_support[candidate_tuple] += 1
        new_frequent_itemsets = {}
        for (itemset_tuple, count) in candidate_support.items():
            if count >= min_support_count:
                new_frequent_itemsets[itemset_tuple] = count
        if not new_frequent_itemsets:
            break
        frequent_itemsets.update(new_frequent_itemsets)
        previous_level = [frozenset(itemset) for itemset in new_frequent_itemsets.keys()]
        k += 1
    return frequent_itemsets
