from collections import deque
def escape_fire_maze(grid: list[str]) -> int:
    n = len(grid)
    if grid[0][0] == 'F' or grid[-1][-1] == 'F':
        return -1
    if n == 1 and grid[0][0] == '.':
        return 0
    directions = [(-1, 0), (1, 0), (0, -1), (0, 1)]
    fire_time = [[float('inf')] * n for _ in range(n)]
    fire_queue = deque()
    for i in range(n):
        for j in range(n):
            if grid[i][j] == 'F':
                fire_time[i][j] = 0
                fire_queue.append((i, j))
    while fire_queue:
        (x, y) = fire_queue.popleft()
        for (dx, dy) in directions:
            (nx, ny) = (x + dx, y + dy)
            if 0 <= nx < n and 0 <= ny < n:
                if grid[nx][ny] == '#' or fire_time[nx][ny] != float('inf'):
                    continue
                fire_time[nx][ny] = fire_time[x][y] + 1
                fire_queue.append((nx, ny))
    agent_queue = deque()
    visited = [[False] * n for _ in range(n)]
    agent_queue.append((0, 0, 0))
    visited[0][0] = True
    while agent_queue:
        (x, y, time) = agent_queue.popleft()
        if x == n - 1 and y == n - 1:
            return time
        for (dx, dy) in directions:
            (nx, ny) = (x + dx, y + dy)
            if 0 <= nx < n and 0 <= ny < n:
                if grid[nx][ny] == '#' or visited[nx][ny]:
                    continue
                if fire_time[nx][ny] > time + 1 or (nx == n - 1 and ny == n - 1 and (fire_time[nx][ny] >= time + 1)):
                    visited[nx][ny] = True
                    agent_queue.append((nx, ny, time + 1))
    return -1
