import numpy as np
def bayesian_linear_regression_pred(X: np.ndarray, y: np.ndarray, X_new: np.ndarray, mu: float | np.ndarray=0.0, sigma: float=1.0, V: float | np.ndarray | None=None, fit_intercept: bool=True) -> list[float]:
    """Bayesian linear regression with known variance.

    The function must return the MAP predictive means for every row in
    `X_new`.  See the task description for full details.
    """
    if fit_intercept:
        X = np.column_stack([np.ones(X.shape[0]), X])
        X_new = np.column_stack([np.ones(X_new.shape[0]), X_new])
    (N, M) = X.shape
    if isinstance(mu, (float, int)):
        mu = np.full(M, mu)
    elif isinstance(mu, np.ndarray) and mu.ndim == 0:
        mu = np.full(M, mu.item())
    elif isinstance(mu, np.ndarray) and mu.ndim == 1:
        if len(mu) != M:
            raise ValueError(f'mu must have length {M} when fit_intercept is {fit_intercept}')
    else:
        raise ValueError('mu must be a scalar or a 1-D array')
    if V is None:
        V = np.eye(M)
    elif isinstance(V, (float, int)):
        V = V * np.eye(M)
    elif isinstance(V, np.ndarray):
        if V.ndim == 0:
            V = V.item() * np.eye(M)
        elif V.ndim == 1:
            if len(V) != M:
                raise ValueError(f'V as 1-D array must have length {M}')
            V = np.diag(V)
        elif V.ndim == 2:
            if V.shape != (M, M):
                raise ValueError(f'V as 2-D array must be of shape ({M}, {M})')
        else:
            raise ValueError('V must be a scalar, 1-D, or 2-D array')
    else:
        raise ValueError('V must be a scalar, 1-D array, 2-D array, or None')
    V_inv = np.linalg.inv(V)
    XT_X = X.T @ X
    Sigma_p = np.linalg.inv(V_inv + XT_X)
    mu_p = Sigma_p @ (V_inv @ mu + X.T @ y)
    y_pred = X_new @ mu_p
    y_pred_rounded = [round(val, 4) for val in y_pred]
    return y_pred_rounded
