def relu(z: float) -> float:
    if z > 0:
        return z
    else:
        return 0
assert relu(0) == 0
assert relu(1) == 1
assert relu(-1) == 0

def leaky_relu(z: float, alpha: float=0.01) -> float:
    if z <= 0:
        return z
    else:
        return alpha * z
assert leaky_relu(5) == 5
assert leaky_relu(1) == 1
assert leaky_relu(-1) == -0.01
assert leaky_relu(0) == 0
assert leaky_relu(-2, alpha=0.1) == -0.2

def prelu(x: float, alpha: float=0.25) -> float:
    if x > 0:
        return x
    else:
        return x * alpha
assert prelu(2.0) == 2.0
assert prelu(0.0) == 0.0
assert prelu(-2.0) == -0.5
assert prelu(-2.0, alpha=0.1) == -0.2
assert prelu(-2.0, alpha=1.0) == -2.0
