def transpose_matrix(a: list[list[int | float]]) -> list[list[int | float]]:
    if not a:
        return []
    return [[row[j] for row in a] for j in range(len(a[0]))]

import numpy as np
def reshape_matrix(a: list[list[int | float]], new_shape: tuple[int, int]) -> list[list[int | float]]:
    total_elements = len(a) * len(a[0])
    new_total = new_shape[0] * new_shape[1]
    if total_elements != new_total:
        return []
    arr = np.array(a)
    reshaped = arr.reshape(new_shape)
    return reshaped.tolist()

def calculate_matrix_mean(matrix: list[list[float]], mode: str) -> list[float]:
    if mode == 'row':
        return [sum(row) / len(row) for row in matrix]
    elif mode == 'column':
        num_rows = len(matrix)
        if num_rows == 0:
            return []
        num_cols = len(matrix[0])
        result = []
        for col in range(num_cols):
            total = 0
            for row in matrix:
                total += row[col]
            mean = total / num_rows
            result.append(mean)
        return result
    else:
        raise ValueError("Mode must be 'row' or 'column'")

def scalar_multiply(matrix: list[list[int | float]], scalar: int | float) -> list[list[int | float]]:
    new_matrix = []
    for row in matrix:
        new_row = [element * scalar for element in row]
        new_matrix.append(new_row)
    return new_matrix

