def batch_iterator(X, y=None, batch_size=64):
    n_samples = X.shape[0] if y is None else y.shape[0]
    for i in range(0, n_samples, batch_size):
        end = i + batch_size
        if y is None:
            batch = X[i:end]
        else:
            batch = (X[i:end], y[i:end])
        if y is not None:
            yield (batch[0].tolist(), batch[1].tolist())
        else:
            yield batch.tolist()

