import numpy as np


def gini_impurity(y):
    _, counts = np.unique(y, return_counts=True)
    total = len(y)
    impurity = 1 - np.sum((counts / total) ** 2)
    return impurity


def weighted_gini_impurity(y_left, y_right):
    n_left = len(y_left)
    n_right = len(y_right)
    n_total = n_left + n_right
    gini_left = gini_impurity(y_left)
    gini_right = gini_impurity(y_right)
    weighted_gini = (n_left / n_total) * gini_left + (n_right / n_total) * gini_right
    return weighted_gini


def find_best_split(X, y):
    best_gain = 0
    best_feature = None
    best_threshold = None

    for feature in range(X.shape[1]):
        sorted_indices = np.argsort(X[:, feature])
        sorted_X = X[sorted_indices]
        sorted_y = y[sorted_indices]

        for i in range(len(sorted_X) - 1):
            threshold = (sorted_X[i] + sorted_X[i + 1]) / 2
            left_indices = sorted_indices[:i + 1]
            right_indices = sorted_indices[i + 1:]
            left_y = y[left_indices]
            right_y = y[right_indices]

            gain = gini_impurity(y) - weighted_gini_impurity(left_y, right_y)

            if gain > best_gain:
                best_gain = gain
                best_feature = feature
                best_threshold = threshold

    return best_feature, best_threshold


class TreeNode:
    def __init__(self, feature=None, threshold=None, left=None, right=None, label=None):
        self.feature = feature
        self.threshold = threshold
        self.left = left
        self.right = right
        self.label = label


def build_tree(X, y, depth=0, max_depth=None):
    if max_depth is not None and depth >= max_depth:
        label, _ = np.unique(y, return_counts=True)
        majority_label = label[np.argmax(_)]
        return TreeNode(label=majority_label)

    if len(np.unique(y)) == 1:
        return TreeNode(label=y[0])

    best_feature, best_threshold = find_best_split(X, y)

    if best_feature is None:
        label, _ = np.unique(y, return_counts=True)
        majority_label = label[np.argmax(_)]
        return TreeNode(label=majority_label)

    left_indices = X[:, best_feature] <= best_threshold
    right_indices = X[:, best_feature] > best_threshold

    left_child = build_tree(X[left_indices], y[left_indices], depth + 1, max_depth)
    right_child = build_tree(X[right_indices], y[right_indices], depth + 1, max_depth)

    return TreeNode(feature=best_feature, threshold=best_threshold, left=left_child, right=right_child)


def predict(tree, X):
    predictions = []
    for sample in X:
        current_node = tree
        while current_node.label is None:
            if sample[current_node.feature] <= current_node.threshold:
                current_node = current_node.left
            else:
                current_node = current_node.right
        predictions.append(current_node.label)
    return predictions


def decision_tree_predict(
    X_train: np.ndarray,
    y_train: np.ndarray,
    X_test: np.ndarray,
    max_depth: int | None = None,
) -> list[int]:
    tree = build_tree(X_train, y_train, max_depth=max_depth)
    return predict(tree, X_test)


