import itertools


def apriori_frequent_itemsets(transactions: list[list[int]], min_sup: float) -> list[tuple]:
    """Find frequent itemsets with the Apriori algorithm.

    Parameters
    ----------
    transactions : list[list[Hashable]]
        List of transactions; each transaction is itself a list of items.
    min_sup : float
        Minimum support threshold expressed as a fraction (>0 and ≤1).

    Returns
    -------
    list[tuple]
        Frequent itemsets ordered by length and then lexicographically.
    """
    num_transactions = len(transactions)
    frequent_itemsets = []

    # Step 1: Find frequent 1-itemsets
    item_counts = {}
    for transaction in transactions:
        for item in transaction:
            if item not in item_counts:
                item_counts[item] = 1
            else:
                item_counts[item] += 1

    frequent_1_itemsets = [(item,) for item, count in item_counts.items() if count / num_transactions >= min_sup]
    frequent_itemsets.extend(frequent_1_itemsets)

    k = 2
    while frequent_1_itemsets:
        candidate_k_itemsets = []
        for itemset1 in frequent_1_itemsets:
            for itemset2 in frequent_1_itemsets:
                joined = itemset1 + itemset2
                if len(joined) == k and all(subset in frequent_itemsets for subset in itertools.combinations(joined, k - 1)):
                    candidate_k_itemsets.append(tuple(sorted(joined)))

        candidate_k_itemsets = list(set(candidate_k_itemsets))

        itemset_supports = {}
        for transaction in transactions:
            for candidate in candidate_k_itemsets:
                if all(item in transaction for item in candidate):
                    if candidate not in itemset_supports:
                        itemset_supports[candidate] = 1
                    else:
                        itemset_supports[candidate] += 1

        frequent_k_itemsets = [(itemset,) for itemset, count in itemset_supports.items() if count / num_transactions >= min_sup]
        frequent_itemsets.extend(frequent_k_itemsets)

        frequent_1_itemsets = frequent_k_itemsets
        k += 1

    return frequent_itemsets


