from collections import Counter, defaultdict
import itertools


class TreeNode:
    def __init__(self, item, count):
        self.item = item
        self.count = count
        self.children = {}
        self.parent = None


def build_fptree(transactions, min_sup):
    item_count = Counter()
    for transaction in transactions:
        item_count.update(transaction)

    frequent_items = [item for item, count in item_count.items() if count >= min_sup]
    frequent_items.sort(key=lambda x: item_count[x], reverse=True)

    header_table = defaultdict(list)
    for item in frequent_items:
        header_table[item].append(None)

    fptree = TreeNode(None, 1)
    for transaction in transactions:
        filtered_transaction = [item for item in transaction if item in frequent_items]
        if filtered_transaction:
            current_node = fptree
            for item in filtered_transaction:
                if item not in current_node.children:
                    current_node.children[item] = TreeNode(item, 1)
                else:
                    current_node.children[item].count += 1
                current_node = current_node.children[item]
                header_table[item].append(current_node)

    return fptree, header_table


def mine_patterns(header_table, prefix, min_sup):
    frequent_itemsets = []
    sorted_items = sorted(header_table.keys(), key=lambda x: header_table[x][0].count, reverse=True)
    for item in sorted_items:
        new_prefix = prefix + (item,)
        support = header_table[item][0].count
        if support >= min_sup:
            frequent_itemsets.append(new_prefix)
            conditional_pattern_base = []
            for node in header_table[item][1:]:
                path = []
                current = node
                while current.parent:
                    path.append(current.item)
                    current = current.parent
                conditional_pattern_base.append(path[::-1])
            conditional_fptree, conditional_header_table = build_fptree(conditional_pattern_base, min_sup)
            if conditional_fptree.children:
                frequent_itemsets.extend(mine_patterns(conditional_header_table, new_prefix, min_sup))
    return frequent_itemsets


def fp_growth(transactions, min_sup):
    fptree, header_table = build_fptree(transactions, min_sup)
    all_frequent_itemsets = mine_patterns(header_table, (), min_sup)
    all_frequent_itemsets.sort(key=lambda x: (len(x), sorted(x)))
    return all_frequent_itemsets


