import numpy as np
import math


def detect_outliers(data: np.ndarray, n_trees: int, sample_size: int, epsilon: float) -> list[int]:
    """Tiny Isolation Forest implementation.

    Args:
        data: 2-D NumPy array where each row is an observation and each column a feature.
        n_trees: Number of random isolation trees that will be grown.
        sample_size: Number of samples drawn (without replacement) to grow every tree.
        epsilon: Fraction (0 ≤ ε < 1) of observations that are considered outliers.

    Returns:
        A list with the zero-based indices of the detected outliers, sorted increasingly.
    """
    np.random.seed(42)
    n, _ = data.shape
    phi = 2 * math.log(n - 1) - 2 * (n - 1) / n

    outlier_scores = []
    for _ in range(n_trees):
        if sample_size >= n:
            subsample = data
        else:
            subsample_indices = np.random.choice(n, sample_size, replace=False)
            subsample = data[subsample_indices]

        tree = build_isolation_tree(subsample)
        path_lengths = compute_path_lengths(data, tree)
        mean_path_lengths = np.mean(path_lengths, axis=1)
        scores = 2 ** (-mean_path_lengths / phi)
        outlier_scores.append(scores)

    combined_scores = np.mean(outlier_scores, axis=0)
    num_outliers = int(epsilon * n)
    if num_outliers == 0:
        return []
    outlier_indices = np.argsort(combined_scores)[-num_outliers:]
    return outlier_indices.tolist()


def build_isolation_tree(data):
    n, _ = data.shape
    height_limit = math.ceil(math.log2(n))
    root = build_node(data, 0, height_limit)
    return root


def build_node(data, depth, height_limit):
    n, _ = data.shape
    if n <= 1 or depth >= height_limit:
        return {'is_leaf': True, 'n': n}

    feature_index = np.random.randint(data.shape[1])
    threshold = np.random.uniform(np.min(data[:, feature_index]), np.max(data[:, feature_index]))
    left_mask = data[:, feature_index] < threshold
    right_mask = ~left_mask

    left_node = build_node(data[left_mask], depth + 1, height_limit)
    right_node = build_node(data[right_mask], depth + 1, height_limit)

    return {'is_leaf': False, 'feature_index': feature_index, 'threshold': threshold,
            'left': left_node, 'right': right_node}


def compute_path_lengths(data, tree):
    path_lengths = []
    for observation in data:
        path_length = compute_path_length(observation, tree)
        path_lengths.append(path_length)
    return path_lengths


def compute_path_length(observation, tree, depth=0):
    if tree['is_leaf']:
        n = tree['n']
        if n == 1:
            return depth
        else:
            return depth + 2 * math.log(n - 1) + 0.5772156649 - 2 * (n - 1) / n

    if observation[tree['feature_index']] < tree['threshold']:
        return compute_path_length(observation, tree['left'], depth + 1)
    else:
        return compute_path_length(observation, tree['right'], depth + 1)


