def matrix_dot_vector(a: list[list[int | float]], b: list[int | float]) -> list[int | float] | int:
    if not a:
        if len(b) == 0:
            return []
        else:
            return -1
    cols = len(a[0])
    for row in a:
        if len(row) != cols:
            return -1
    if len(b) != cols:
        return -1
    result = []
    for row in a:
        dot_product = 0.0
        for i in range(cols):
            dot_product += row[i] * b[i]
        if dot_product.is_integer():
            result.append(int(dot_product))
        else:
            result.append(dot_product)
    return result
