import numpy as np
def transform_matrix(A: list[list[int | float]], T: list[list[int | float]], S: list[list[int | float]]) -> list[list[int | float]]:
    try:
        A_np = np.array(A, dtype=np.float64)
        T_np = np.array(T, dtype=np.float64)
        S_np = np.array(S, dtype=np.float64)
    except (ValueError, TypeError):
        return -1
    if T_np.shape[0] != T_np.shape[1]:
        return -1
    if S_np.shape[0] != S_np.shape[1]:
        return -1
    try:
        T_inv = np.linalg.inv(T_np)
    except np.linalg.LinAlgError:
        return -1
    try:
        np.linalg.inv(S_np)
    except np.linalg.LinAlgError:
        return -1
    t_rows = T_np.shape[0]
    (a_rows, a_cols) = A_np.shape
    if t_rows != a_rows:
        return -1
    s_rows = S_np.shape[0]
    if a_cols != s_rows:
        return -1
    try:
        result = T_inv @ A_np @ S_np
    except ValueError:
        return -1
    result_rounded = np.round(result, decimals=4)
    return result_rounded.tolist()
