def inverse_2x2(matrix: list[list[float]]) -> list[list[float]]:
    if len(matrix) != 2:
        return None
    for row in matrix:
        if len(row) != 2:
            return None
    (a, b) = (matrix[0][0], matrix[0][1])
    (c, d) = (matrix[1][0], matrix[1][1])
    determinant = a * d - b * c
    if determinant == 0:
        return None
    inv_determinant = 1.0 / determinant
    inverse = [[d * inv_determinant, -b * inv_determinant], [-c * inv_determinant, a * inv_determinant]]
    return inverse
