def matrixmul(a: list[list[int | float]], b: list[list[int | float]]) -> list[list[int | float]]:
    if len(a) == 0:
        a_rows = 0
        a_cols = 0
    else:
        a_rows = len(a)
        a_cols = len(a[0])
        for row in a:
            if len(row) != a_cols:
                return -1
    if len(b) == 0:
        b_rows = 0
        b_cols = 0
    else:
        b_rows = len(b)
        b_cols = len(b[0])
        for row in b:
            if len(row) != b_cols:
                return -1
    if a_cols != b_rows:
        return -1
    result = []
    for i in range(a_rows):
        current_row = []
        for j in range(b_cols):
            dot_product = 0.0
            for k in range(a_cols):
                dot_product += a[i][k] * b[k][j]
            current_row.append(dot_product)
        result.append(current_row)
    return result
