import math
from collections import Counter
def learn_decision_tree(examples: list[dict], attributes: list[str], target_attr: str) -> dict:

    def entropy(examples):
        """Calculate the entropy of the given examples based on the target attribute."""
        if not examples:
            return 0.0
        target_values = [ex[target_attr] for ex in examples]
        value_counts = Counter(target_values)
        total = len(examples)
        return -sum((count / total * math.log2(count / total) for count in value_counts.values()))

    def information_gain(attr):
        """Calculate the information gain of splitting on the given attribute."""
        original_entropy = entropy(examples)
        subsets = {}
        for ex in examples:
            val = ex[attr]
            if val not in subsets:
                subsets[val] = []
            subsets[val].append(ex)
        total_examples = len(examples)
        weighted_entropy = 0.0
        for subset in subsets.values():
            subset_size = len(subset)
            if subset_size == 0:
                continue
            weighted_entropy += subset_size / total_examples * entropy(subset)
        return original_entropy - weighted_entropy
    target_values = [ex[target_attr] for ex in examples]
    if len(set(target_values)) == 1:
        return {'leaf': target_values[0]}
    if not attributes:
        majority_value = Counter(target_values).most_common(1)[0][0]
        return {'leaf': majority_value}
    best_attr = max(attributes, key=lambda attr: information_gain(attr))
    attribute_subsets = {}
    for ex in examples:
        attr_val = ex[best_attr]
        if attr_val not in attribute_subsets:
            attribute_subsets[attr_val] = []
        attribute_subsets[attr_val].append(ex)
    remaining_attributes = [attr for attr in attributes if attr != best_attr]
    children = {}
    current_majority = Counter(target_values).most_common(1)[0][0]
    for (attr_val, subset) in attribute_subsets.items():
        if not subset:
            children[attr_val] = {'leaf': current_majority}
        else:
            children[attr_val] = learn_decision_tree(subset, remaining_attributes, target_attr)
    return {'attribute': best_attr, 'children': children}
