import numpy as np
def isolation_tree_path_length(data: np.ndarray) -> list[int]:
    """Compute the depth of the leaf reached by every sample in a random isolation tree.

    The tree is built following the rules described in the task statement and the
    random seed must be set to 0 so that results are reproducible.

    Args:
        data: A 2-D NumPy array of shape (n_samples, n_features).

    Returns:
        A list of length *n_samples* where the *i-th* element is the number of
        edges from the root to the leaf that contains the *i-th* sample.
    """
    np.random.seed(0)
    n_samples = data.shape[0]
    if n_samples == 0:
        return []

    class LeafNode:

        def __init__(self, indices, depth):
            self.indices = indices
            self.depth = depth

    class InternalNode:

        def __init__(self, feature, split_value, left, right):
            self.feature = feature
            self.split_value = split_value
            self.left = left
            self.right = right

    def build_node(indices, depth):
        if len(indices) < 3:
            return LeafNode(indices, depth)
        n_features = data.shape[1]
        feature = np.random.choice(n_features)
        values = data[indices, feature]
        down = np.min(values)
        up = np.max(values)
        if down == up:
            return LeafNode(indices, depth)
        split_value = np.random.uniform(low=down, high=up)
        left_indices = []
        right_indices = []
        for idx in indices:
            if data[idx, feature] <= split_value:
                left_indices.append(idx)
            else:
                right_indices.append(idx)
        left_child = build_node(left_indices, depth + 1)
        right_child = build_node(right_indices, depth + 1)
        return InternalNode(feature, split_value, left_child, right_child)
    root = build_node(list(range(n_samples)), 0)
    depths = []
    for sample_idx in range(n_samples):
        current_node = root
        while True:
            if isinstance(current_node, LeafNode):
                depths.append(current_node.depth)
                break
            else:
                feature = current_node.feature
                split_value = current_node.split_value
                if data[sample_idx, feature] <= split_value:
                    current_node = current_node.left
                else:
                    current_node = current_node.right
    return depths

import math
from collections import Counter
from itertools import combinations
from typing import List, Dict, Tuple, Hashable
def find_frequent_itemsets(transactions: List[List[Hashable]], minsup: float) -> Dict[Tuple[Hashable, ...], int]:
    """Return all frequent itemsets in **transactions**.

    Args:
        transactions: List where each element represents a transaction and is
                       itself a list of items (hashable Python objects).
        minsup:       Minimum support given as a fraction between 0 and 1.

    Returns:
        A dictionary that maps every frequent itemset (stored as a tuple of
        sorted items) to its absolute support count.  If no itemset reaches
        the threshold the function must return an empty dictionary.
    """
    if not transactions:
        return {}
    N = len(transactions)
    min_support_count = math.ceil(minsup * N)
    transactions_set = [set(transaction) for transaction in transactions]
    item_counts = Counter()
    for t in transactions_set:
        item_counts.update(t)
    L1 = {(item,): count for (item, count) in item_counts.items() if count >= min_support_count}
    if not L1:
        return {}
    result = L1.copy()
    current_L = L1
    k = 2
    while True:
        itemsets = list(current_L.keys())
        if len(itemsets) < 2:
            break
        Ck = []
        for i in range(len(itemsets)):
            for j in range(i + 1, len(itemsets)):
                l1 = itemsets[i]
                l2 = itemsets[j]
                if l1[:-1] == l2[:-1]:
                    candidate = l1 + (l2[-1],)
                    Ck.append(candidate)
        pruned_Ck = []
        for candidate in Ck:
            all_subsets = [candidate[:i] + candidate[i + 1:] for i in range(k)]
            if all((s in current_L for s in all_subsets)):
                pruned_Ck.append(candidate)
        candidate_support = {}
        for candidate in pruned_Ck:
            cnt = 0
            candidate_set = set(candidate)
            for t in transactions_set:
                if candidate_set.issubset(t):
                    cnt += 1
            if cnt >= min_support_count:
                candidate_support[candidate] = cnt
        if not candidate_support:
            break
        result.update(candidate_support)
        current_L = candidate_support
        k += 1
    return result

import numpy as np
def kmeans(data: np.ndarray, k: int, epsilon: float=0.001, max_iter: int=2000) -> tuple[list[list[float]], list[int]]:
    """Cluster *data* into *k* groups using the K-Means algorithm."""
    centers = data[:k].copy()
    for _ in range(max_iter):
        labels = []
        for sample in data:
            distances = np.sum((sample - centers) ** 2, axis=1)
            labels.append(np.argmin(distances))
        labels = np.array(labels)
        new_centers = np.zeros_like(centers)
        for i in range(k):
            cluster_samples = data[labels == i]
            if len(cluster_samples) == 0:
                random_idx = np.random.randint(0, data.shape[0])
                new_centers[i] = data[random_idx]
            else:
                new_centers[i] = np.mean(cluster_samples, axis=0)
        max_change = np.max(np.linalg.norm(new_centers - centers, axis=1))
        if max_change < epsilon:
            centers = new_centers
            break
        centers = new_centers.copy()
    final_labels = []
    for sample in data:
        distances = np.sum((sample - centers) ** 2, axis=1)
        final_labels.append(np.argmin(distances))
    centers_rounded = [list(np.round(center, 4)) for center in centers]
    return (centers_rounded, final_labels)

