import math
from collections import defaultdict
def adaboost_1d_predict(x_train: list[float], y_train: list[int], x_test: list[float], epsilon: float=0.0) -> list[int]:
    """Trains a 1-D AdaBoost ensemble and returns predictions."""
    N = len(x_train)
    if N == 0:
        return []
    M = len(x_test)
    if M == 0:
        return []
    weights = np.ones(N) / N
    sorted_x = sorted(x_train)
    thresholds = [(sorted_x[i] + sorted_x[i + 1]) / 2 for i in range(len(sorted_x) - 1)]
    stumps = [(d, theta) for theta in thresholds for d in [0, 1]]
    ensemble = []
    while True:
        min_error = float('inf')
        best_stump = None
        best_preds = None
        for (d, theta) in stumps:
            preds = []
            for x in x_train:
                if d == 0:
                    pred = 1 if x <= theta else -1
                else:
                    pred = 1 if x > theta else -1
                preds.append(pred)
            error = sum((weights[i] for i in range(N) if preds[i] != y_train[i]))
            if error < min_error:
                min_error = error
                best_stump = (d, theta)
                best_preds = preds
        if best_stump is None:
            break
        if min_error <= 0:
            alpha = float('inf')
        else:
            alpha = 0.5 * math.log((1 - min_error) / min_error)
        ensemble.append((alpha, best_stump))
        for i in range(N):
            y = y_train[i]
            h = best_preds[i]
            weights[i] *= math.exp(-alpha * y * h)
        total_weight = sum(weights)
        if total_weight == 0:
            break
        weights /= total_weight
        errors = 0
        for i in range(N):
            x = x_train[i]
            total = 0.0
            for (alpha_t, (d_t, theta_t)) in ensemble:
                if d_t == 0:
                    h_t = 1 if x <= theta_t else -1
                else:
                    h_t = 1 if x > theta_t else -1
                total += alpha_t * h_t
            pred = 1 if total >= 0 else -1
            if pred != y_train[i]:
                errors += 1
        train_error = errors / N
        if train_error <= epsilon:
            break
    predictions = []
    for x in x_test:
        total = 0.0
        for (alpha_t, (d_t, theta_t)) in ensemble:
            if d_t == 0:
                h_t = 1 if x <= theta_t else -1
            else:
                h_t = 1 if x > theta_t else -1
            total += alpha_t * h_t
        pred = 1 if total >= 0 else -1
        predictions.append(pred)
    return predictions

import numpy as np
def softplus(x, deriv: bool=False):
    """Implements the SoftPlus activation function and its gradient (sigmoid)."""
    x_np = np.asarray(x, dtype=float)
    if deriv:
        result_np = 1 / (1 + np.exp(-x_np))
    else:
        result_np = np.where(x_np > 709, x_np, np.log1p(np.exp(x_np)))
    rounded_np = np.around(result_np, decimals=4)
    if x_np.ndim == 0:
        return rounded_np.item()
    else:
        return rounded_np.tolist()

import numpy as np
from collections import Counter
def unsmoothed_ngram_log_prob(corpus: list[str], sequence: list[str], N: int) -> float:
    """Compute the unsmoothed MLE N-gram log-probability of *sequence*.

    Your task is to complete this function so that it builds N-gram count
    tables from *corpus* and then returns the total log-probability of
    *sequence* under the resulting unsmoothed language model.

    The return value must be rounded to 4 decimal places.  If any required
    count is zero you should immediately return ``float('-inf')``.
    """
    if len(sequence) < N:
        return round(0.0, 4)
    if N == 1:
        total_tokens = len(corpus)
        if total_tokens == 0:
            return float('-inf')
        counts = Counter(corpus)
        for word in sequence:
            if counts.get(word, 0) == 0:
                return float('-inf')
        total_log = 0.0
        for word in sequence:
            prob = counts[word] / total_tokens
            total_log += np.log(prob)
        return round(total_log, 4)
    else:
        corpus_ngrams = []
        if len(corpus) >= N:
            corpus_ngrams = [tuple(corpus[i:i + N]) for i in range(len(corpus) - N + 1)]
        ngram_counts = Counter(corpus_ngrams)
        corpus_prefixes = []
        if len(corpus) >= N - 1:
            corpus_prefixes = [tuple(corpus[i:i + N - 1]) for i in range(len(corpus) - (N - 1) + 1)]
        prefix_counts = Counter(corpus_prefixes)
        sequence_ngrams = [tuple(sequence[i:i + N]) for i in range(len(sequence) - N + 1)]
        total_log = 0.0
        for ngram in sequence_ngrams:
            cnt_ngram = ngram_counts.get(ngram, 0)
            if cnt_ngram == 0:
                return float('-inf')
            prefix = ngram[:-1]
            cnt_prefix = prefix_counts.get(prefix, 0)
            if cnt_prefix == 0:
                return float('-inf')
            total_log += np.log(cnt_ngram / cnt_prefix)
        return round(total_log, 4)

def forward_algorithm(S: list[float], A: list[list[float]], B: list[list[float]], observations: list[int]) -> float:
    """Forward algorithm for Hidden Markov Models.

    Args:
        S (list[float]): Initial state probabilities.
        A (list[list[float]]): State–transition probabilities.
        B (list[list[float]]): Emission probabilities.
        observations (list[int]): Observation index sequence.

    Returns:
        float: Sequence likelihood rounded to 4 decimals, or −1 on invalid input.
    """
    if not observations:
        return -1
    if not S:
        return -1
    n = len(S)
    if len(A) != n:
        return -1
    for row in A:
        if len(row) != n:
            return -1
    if len(B) != n:
        return -1
    if n == 0:
        return -1
    m = len(B[0]) if B else 0
    for row in B:
        if len(row) != m:
            return -1
    for obs in observations:
        if obs < 0 or obs >= m:
            return -1
    T = len(observations)
    alpha_prev = [S[i] * B[i][observations[0]] for i in range(n)]
    for t in range(1, T):
        current_obs = observations[t]
        alpha_current = [0.0] * n
        for j in range(n):
            transition_sum = sum((alpha_prev[i] * A[i][j] for i in range(n)))
            alpha_current[j] = transition_sum * B[j][current_obs]
        alpha_prev = alpha_current
    total_probability = sum(alpha_prev)
    return round(total_probability, 4)

import numpy as np
TIME_STEPS = 20
def string_to_int(text: str, time_steps: int, vocabulary: dict[str, int]) -> list[int]:
    """Encodes a text string into a fixed-length list of integer token ids using the provided vocabulary."""
    encoded = [vocabulary.get(char, 0) for char in text]
    if len(encoded) > time_steps:
        encoded = encoded[:time_steps]
    else:
        encoded += [0] * (time_steps - len(encoded))
    return encoded
def int_to_string(indices, inverse_vocab: dict[int, str]) -> str:
    """Converts a list/array of integer ids back to a human-readable string, ignoring padding tokens (0)."""
    if isinstance(indices, np.ndarray):
        indices = indices.tolist()
    return ''.join([inverse_vocab.get(idx, '') for idx in indices if idx != 0])
def run_example(model, input_vocabulary: dict[str, int], inv_output_vocabulary: dict[int, str], text: str) -> str:
    """End-to-end example: encodes text, runs model prediction, and decodes the output."""
    encoded_sequence = string_to_int(text, TIME_STEPS, input_vocabulary)
    encoded_batch = np.array([encoded_sequence], dtype=np.int64)
    model_output = model.predict(encoded_batch)
    predicted_ids = np.argmax(model_output, axis=-1)[0]
    return int_to_string(predicted_ids, inv_output_vocabulary)

import numpy as np
def adaboost_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, n_clf: int=5) -> list[int]:
    """Train AdaBoost with decision stumps and predict labels for X_test."""
    n_clf = max(n_clf, 1)
    (m, n) = X_train.shape
    k = X_test.shape[0]
    if m == 0:
        return [1] * k
    weights = np.ones(m) / m
    stumps = []
    alphas = []
    for _ in range(n_clf):
        best_error = float('inf')
        best_stump = None
        for feature in range(n):
            sorted_indices = np.argsort(X_train[:, feature])
            sorted_x = X_train[sorted_indices, feature]
            sorted_y = y_train[sorted_indices]
            sorted_weights = weights[sorted_indices]
            cum_pos = np.zeros(m + 1)
            cum_neg = np.zeros(m + 1)
            for i in range(m):
                if sorted_y[i] == 1:
                    cum_pos[i + 1] = cum_pos[i] + sorted_weights[i]
                    cum_neg[i + 1] = cum_neg[i]
                else:
                    cum_neg[i + 1] = cum_neg[i] + sorted_weights[i]
                    cum_pos[i + 1] = cum_pos[i]
            total_pos = cum_pos[m]
            total_neg = cum_neg[m]
            for split in range(m + 1):
                error_p1 = cum_pos[split] + (total_neg - cum_neg[split])
                error_pm1 = cum_neg[split] + (total_pos - cum_pos[split])
                if split == 0:
                    threshold = sorted_x[0] - 1.0 if m > 0 else 0.0
                elif split == m:
                    threshold = sorted_x[-1] + 1.0 if m > 0 else 0.0
                else:
                    threshold = (sorted_x[split - 1] + sorted_x[split]) / 2.0
                if error_p1 < best_error:
                    best_error = error_p1
                    best_stump = (feature, threshold, 1)
                if error_pm1 < best_error:
                    best_error = error_pm1
                    best_stump = (feature, threshold, -1)
        if best_error <= 0:
            alpha = np.inf
        elif best_error >= 1:
            alpha = -np.inf
        else:
            alpha = 0.5 * np.log((1 - best_error) / best_error)
        stumps.append(best_stump)
        alphas.append(alpha)
        (feature, threshold, polarity) = best_stump
        preds = np.where(X_train[:, feature] < threshold, -1 if polarity == 1 else 1, 1 if polarity == 1 else -1)
        exponent = -alpha * y_train * preds
        weights *= np.exp(exponent)
        weights_sum = np.sum(weights)
        if weights_sum > 0:
            weights /= weights_sum
        else:
            weights = np.ones(m) / m
    predictions = []
    for x in X_test:
        total = 0.0
        for ((feature, threshold, polarity), alpha) in zip(stumps, alphas):
            if x[feature] < threshold:
                h = -1 if polarity == 1 else 1
            else:
                h = 1 if polarity == 1 else -1
            total += alpha * h
        predictions.append(1 if total >= 0 else -1)
    return predictions

from collections import deque
import math
def escape_fire_maze(grid: list[str]) -> int:
    n = len(grid)
    if n == 1:
        return 0 if grid[0][0] == '.' else -1
    if grid[0][0] == '#' or grid[-1][-1] == '#':
        return -1
    fire_time = [[math.inf] * n for _ in range(n)]
    fire_queue = deque()
    for i in range(n):
        for j in range(n):
            if grid[i][j] == 'F':
                fire_time[i][j] = 0
                fire_queue.append((i, j))
    directions = [(-1, 0), (1, 0), (0, -1), (0, 1)]
    while fire_queue:
        (i, j) = fire_queue.popleft()
        for (di, dj) in directions:
            (ni, nj) = (i + di, j + dj)
            if 0 <= ni < n and 0 <= nj < n:
                if grid[ni][nj] != '#' and fire_time[ni][nj] == math.inf:
                    fire_time[ni][nj] = fire_time[i][j] + 1
                    fire_queue.append((ni, nj))
    if fire_time[0][0] == 0 or fire_time[n - 1][n - 1] == 0:
        return -1
    visited = [[math.inf] * n for _ in range(n)]
    visited[0][0] = 0
    q = deque([(0, 0, 0)])
    while q:
        (i, j, t) = q.popleft()
        if i == n - 1 and j == n - 1:
            return t
        for (di, dj) in directions:
            (ni, nj) = (i + di, j + dj)
            if 0 <= ni < n and 0 <= nj < n:
                if grid[ni][nj] == '#':
                    continue
                new_time = t + 1
                if fire_time[ni][nj] > new_time:
                    if visited[ni][nj] > new_time:
                        visited[ni][nj] = new_time
                        q.append((ni, nj, new_time))
    return -1

import numpy as np
def adaboost_predict(X_train: list[list[int | float]], y_train: list[int], X_test: list[list[int | float]], n_estimators: int=10) -> list[int]:
    """Implements AdaBoost with decision stumps as weak learners and returns predictions for X_test."""
    X_train_np = np.array(X_train, dtype=np.float64)
    y_train_np = np.array(y_train, dtype=np.int32)
    y_ada = 2 * y_train_np - 1
    N = len(y_ada)
    if N == 0:
        return []
    weights = np.ones(N) / N
    stumps = []
    for _ in range(n_estimators):
        best_error = np.inf
        best_stump = None
        for feature_index in range(X_train_np.shape[1]):
            X_col = X_train_np[:, feature_index]
            thresholds = np.unique(X_col)
            for threshold in thresholds:
                for polarity in [1, -1]:
                    if polarity == 1:
                        predictions = np.where(X_col < threshold, 1, -1)
                    else:
                        predictions = np.where(X_col >= threshold, 1, -1)
                    error = np.sum(weights * (predictions != y_ada))
                    if error < best_error:
                        best_error = error
                        best_stump = (feature_index, threshold, polarity)
                    elif error == best_error and best_stump is not None:
                        (curr_fi, curr_th, curr_pol) = (feature_index, threshold, polarity)
                        (best_fi, best_th, best_pol) = best_stump
                        if curr_fi < best_fi:
                            best_stump = (curr_fi, curr_th, curr_pol)
                        elif curr_fi == best_fi:
                            if curr_th < best_th:
                                best_stump = (curr_fi, curr_th, curr_pol)
                            elif curr_th == best_th and curr_pol == 1 and (best_pol == -1):
                                best_stump = (curr_fi, curr_th, curr_pol)
        if best_stump is None:
            break
        error_t = best_error
        if error_t <= 0:
            alpha = 0.5 * np.log((1 - error_t) / 1e-10)
        else:
            alpha = 0.5 * np.log((1 - error_t) / (error_t + 1e-10))
        (fi, th, pol) = best_stump
        X_col = X_train_np[:, fi]
        if pol == 1:
            h_x = np.where(X_col < th, 1, -1)
        else:
            h_x = np.where(X_col >= th, 1, -1)
        update_factor = np.exp(-alpha * y_ada * h_x)
        weights *= update_factor
        weights_sum = np.sum(weights)
        if weights_sum > 0:
            weights /= weights_sum
        else:
            weights = np.ones(N) / N
        stumps.append((fi, th, pol, alpha))
        if error_t == 0:
            break
    X_test_np = np.array(X_test, dtype=np.float64)
    predictions = []
    for x in X_test_np:
        total = 0.0
        for (fi, th, pol, alpha) in stumps:
            x_val = x[fi]
            if pol == 1:
                h = 1 if x_val < th else -1
            else:
                h = 1 if x_val >= th else -1
            total += alpha * h
        pred = 1 if total >= 0 else 0
        predictions.append(pred)
    return predictions

import numpy as np
def decision_tree_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, max_depth: int | None=None) -> list[int]:
    """Build a CART decision tree on (X_train, y_train) and predict labels for X_test."""

    def gini(labels: np.ndarray) -> float:
        """Calculate Gini impurity for a set of labels."""
        if len(labels) == 0:
            return 0.0
        (_, counts) = np.unique(labels, return_counts=True)
        probabilities = counts / len(labels)
        return 1.0 - np.sum(probabilities ** 2)

    def find_best_split(X: np.ndarray, y: np.ndarray) -> tuple[int | None, float | None, float]:
        """Find the best feature and threshold to split based on Gini gain."""
        parent_gini_val = gini(y)
        (n_samples, n_features) = X.shape
        best_gain = 0.0
        best_feature = None
        best_threshold = None
        if parent_gini_val == 0:
            return (best_feature, best_threshold, best_gain)
        for feature in range(n_features):
            feats = X[:, feature]
            sorted_indices = np.argsort(feats)
            sorted_feats = feats[sorted_indices]
            sorted_y = y[sorted_indices]
            for i in range(n_samples - 1):
                current_feat = sorted_feats[i]
                next_feat = sorted_feats[i + 1]
                if current_feat == next_feat:
                    continue
                threshold = (current_feat + next_feat) / 2.0
                y_left = sorted_y[:i + 1]
                y_right = sorted_y[i + 1:]
                gini_left = gini(y_left)
                gini_right = gini(y_right)
                weight_left = len(y_left) / n_samples
                weight_right = len(y_right) / n_samples
                child_gini = weight_left * gini_left + weight_right * gini_right
                gain = parent_gini_val - child_gini
                if gain > best_gain:
                    best_gain = gain
                    best_feature = feature
                    best_threshold = threshold
        return (best_feature, best_threshold, best_gain)

    def build_tree(X: np.ndarray, y: np.ndarray, current_depth: int) -> dict:
        """Recursively build the decision tree."""
        unique_labels = np.unique(y)
        if len(unique_labels) == 1:
            return {'type': 'leaf', 'label': unique_labels[0]}
        if max_depth is not None and current_depth >= max_depth:
            majority = np.argmax(np.bincount(y))
            return {'type': 'leaf', 'label': majority}
        (best_feature, best_threshold, best_gain) = find_best_split(X, y)
        if best_gain <= 0:
            majority = np.argmax(np.bincount(y))
            return {'type': 'leaf', 'label': majority}
        left_mask = X[:, best_feature] <= best_threshold
        (X_left, y_left) = (X[left_mask], y[left_mask])
        (X_right, y_right) = (X[~left_mask], y[~left_mask])
        if len(y_left) == 0 or len(y_right) == 0:
            majority = np.argmax(np.bincount(y))
            return {'type': 'leaf', 'label': majority}
        left_child = build_tree(X_left, y_left, current_depth + 1)
        right_child = build_tree(X_right, y_right, current_depth + 1)
        return {'type': 'split', 'feature': best_feature, 'threshold': best_threshold, 'left': left_child, 'right': right_child}

    def predict_sample(x: np.ndarray, tree: dict) -> int:
        """Predict the class of a single sample by traversing the tree."""
        if tree['type'] == 'leaf':
            return tree['label']
        if x[tree['feature']] <= tree['threshold']:
            return predict_sample(x, tree['left'])
        else:
            return predict_sample(x, tree['right'])
    if len(X_train) == 0:
        return [0] * len(X_test)
    decision_tree = build_tree(X_train, y_train, current_depth=0)
    predictions = [predict_sample(x, decision_tree) for x in X_test]
    return predictions

import numpy as np
def metric(name: str):
    """Factory producing a validated distance function.

    The function creates and returns a callable *d* that computes one of four
    classical distances (Euclidean, Manhattan, Chebyshev, Cosine) between two
    numeric vectors.  All numeric outputs are rounded to four decimal places.

    Validation rules inside the returned callable:
    * Both arguments must be one-dimensional, non-empty, equal-length numeric
      iterables (list, tuple or NumPy array).
    * Metric *name* must be one of the supported strings.
    * For the cosine distance, zero-norm vectors are rejected.

    If the metric name is unsupported or validation fails, *d* returns -1.

    Args:
        name (str): Name of the desired metric.

    Returns:
        Callable[[Iterable, Iterable], float | int]: A distance function with
        integrated validation.
    """
    supported_metrics = {'euclidean', 'manhattan', 'chebyshev', 'cosine'}
    if name not in supported_metrics:
        return lambda u, v: -1

    def distance_function(u, v):
        if not (isinstance(u, (list, tuple, np.ndarray)) and isinstance(v, (list, tuple, np.ndarray))):
            return -1
        try:
            u_arr = np.array(u, dtype=float)
            v_arr = np.array(v, dtype=float)
        except (TypeError, ValueError):
            return -1
        if u_arr.ndim != 1 or v_arr.ndim != 1:
            return -1
        if len(u_arr) == 0 or len(v_arr) == 0:
            return -1
        if len(u_arr) != len(v_arr):
            return -1
        if name == 'cosine':
            norm_u = np.linalg.norm(u_arr)
            norm_v = np.linalg.norm(v_arr)
            if norm_u == 0 or norm_v == 0:
                return -1
        try:
            if name == 'euclidean':
                distance = np.linalg.norm(u_arr - v_arr)
            elif name == 'manhattan':
                distance = np.sum(np.abs(u_arr - v_arr))
            elif name == 'chebyshev':
                distance = np.max(np.abs(u_arr - v_arr))
            elif name == 'cosine':
                dot_product = np.dot(u_arr, v_arr)
                cosine_similarity = dot_product / (norm_u * norm_v)
                distance = 1 - cosine_similarity
            else:
                return -1
        except:
            return -1
        return round(distance, 4)
    return distance_function

import numpy as np
def elastic_net_regression(x_train, y_train, x_test, degree=1, reg_factor=0.05, l1_ratio=0.5, n_iterations=3000, learning_rate=0.01):
    """Elastic Net regression implemented with batch gradient descent."""
    try:
        x_train = np.array(x_train, dtype=float)
        y_train = np.array(y_train, dtype=float)
        x_test = np.array(x_test, dtype=float)
    except (ValueError, TypeError):
        return -1
    if n_iterations < 1:
        return -1
    if x_train.ndim == 1:
        x_train = x_train.reshape(1, -1)
    if x_test.ndim == 1:
        x_test = x_test.reshape(1, -1)
    if x_train.shape[1] != x_test.shape[1]:
        return -1
    if y_train.ndim > 1:
        if y_train.shape[0] != x_train.shape[0] or y_train.shape[1] != 1:
            return -1
        y_train = y_train.flatten()
    if len(y_train) != x_train.shape[0]:
        return -1

    def generate_polynomial_features(X, deg):
        if deg < 1:
            return np.zeros((X.shape[0], 0))
        poly_features = []
        for col in X.T:
            for d in range(1, deg + 1):
                poly_features.append(col ** d)
        return np.column_stack(poly_features)
    X_poly_train = generate_polynomial_features(x_train, degree)
    X_poly_test = generate_polynomial_features(x_test, degree)
    if X_poly_train.size == 0:
        X_standardized_train = np.zeros((x_train.shape[0], 0))
        X_standardized_test = np.zeros((x_test.shape[0], 0))
        mean = np.array([])
        std = np.array([])
    else:
        mean = np.mean(X_poly_train, axis=0)
        std = np.std(X_poly_train, axis=0) + 1e-08
        X_standardized_train = (X_poly_train - mean) / std
        X_standardized_test = (X_poly_test - mean) / std
    X_train_processed = np.hstack([np.ones((X_standardized_train.shape[0], 1)), X_standardized_train])
    X_test_processed = np.hstack([np.ones((X_standardized_test.shape[0], 1)), X_standardized_test])
    m = X_train_processed.shape[0]
    p_plus_1 = X_train_processed.shape[1]
    w = np.zeros(p_plus_1)
    for _ in range(n_iterations):
        y_pred = X_train_processed @ w
        error = y_pred - y_train
        mse_gradient = 1 / m * (X_train_processed.T @ error)
        if p_plus_1 > 1:
            l1_term = reg_factor * l1_ratio * np.sign(w[1:])
            l2_term = reg_factor * (1 - l1_ratio) * w[1:]
            reg_gradient = np.zeros_like(w)
            reg_gradient[1:] = l1_term + l2_term
        else:
            reg_gradient = np.zeros_like(w)
        total_gradient = mse_gradient + reg_gradient
        w -= learning_rate * total_gradient
    y_test_pred = X_test_processed @ w
    return np.round(y_test_pred, 4).tolist()

def blackjack_outcome(player: list[int], dealer: list[int]) -> float:
    """Evaluate the outcome of a finished round of Blackjack."""
    sum_p = sum(player)
    aces_p = player.count(1)
    player_score = sum_p + 10 if aces_p > 0 and sum_p + 10 <= 21 else sum_p
    sum_d = sum(dealer)
    aces_d = dealer.count(1)
    dealer_score = sum_d + 10 if aces_d > 0 and sum_d + 10 <= 21 else sum_d
    if player_score > 21:
        return -1.0
    if dealer_score > 21:
        return 1.0
    player_natural = len(player) == 2 and sorted(player) == [1, 10]
    dealer_natural = len(dealer) == 2 and sorted(dealer) == [1, 10]
    if player_natural and dealer_natural:
        return 0.0
    elif player_natural:
        return 1.5
    elif dealer_natural:
        return -1.0
    if player_score > dealer_score:
        return 1.0
    elif player_score < dealer_score:
        return -1.0
    else:
        return 0.0

import numpy as np
def sigmoid(z):
    """Compute the element-wise sigmoid (logistic) function.

    The function must work for scalar numbers, Python lists and NumPy arrays and
    must be numerically stable for very large positive or negative inputs.

    Args:
        z: A scalar (int/float) or array-like object (list or np.ndarray)
           containing numeric values.

    Returns:
        float | list: The sigmoid of the input rounded to 4 decimal places.
        If `z` is a scalar the return value is a float, otherwise it is a Python
        list with the same shape as the input.
    """
    if isinstance(z, (int, float)):
        if z >= 0:
            result = 1 / (1 + np.exp(-z))
        else:
            result = np.exp(z) / (1 + np.exp(z))
        return round(result, 4)
    else:
        arr = np.array(z)
        sigmoid_vals = np.where(arr >= 0, 1 / (1 + np.exp(-arr)), np.exp(arr) / (1 + np.exp(arr)))
        return np.around(sigmoid_vals, decimals=4).tolist()

import math
import bisect
from collections import Counter
import numpy as np
def best_split(feature, target):
    """Determine the numerical threshold that produces the highest information gain.

    Parameters
    ----------
    feature : list[int | float] | 1-D numpy.ndarray
        Numerical values of a single attribute.
    target  : list[int] | 1-D numpy.ndarray
        Corresponding class labels.

    Returns
    -------
    tuple
        (threshold, information_gain) where
        * threshold – float rounded to 4 decimals or None when no useful split exists;
        * information_gain – float rounded to 4 decimals.
    """
    if isinstance(feature, np.ndarray):
        feature = feature.tolist()
    if isinstance(target, np.ndarray):
        target = target.tolist()
    n = len(feature)
    if n == 0 or len(target) != n:
        return (None, 0.0)
    sorted_pairs = sorted(zip(feature, target), key=lambda x: x[0])
    sorted_x = [p[0] for p in sorted_pairs]
    sorted_y = [p[1] for p in sorted_pairs]
    sorted_unique_x = []
    prev = None
    for x in sorted_x:
        if x != prev:
            sorted_unique_x.append(x)
            prev = x
    if len(sorted_unique_x) < 2:
        return (None, 0.0)

    def entropy(labels):
        if not labels:
            return 0.0
        counts = Counter(labels)
        total = len(labels)
        h = 0.0
        for cnt in counts.values():
            p = cnt / total
            h -= p * math.log2(p)
        return h
    parent_entropy = entropy(target)
    if parent_entropy == 0:
        return (None, 0.0)
    thresholds = []
    for i in range(len(sorted_unique_x) - 1):
        t = (sorted_unique_x[i] + sorted_unique_x[i + 1]) / 2
        thresholds.append(t)
    max_ig = -math.inf
    best_t = None
    len_target = len(target)
    for t in thresholds:
        split_idx = bisect.bisect_left(sorted_x, t)
        len_left = split_idx
        len_right = len_target - split_idx
        if len_left == 0 or len_right == 0:
            current_ig = 0.0
        else:
            left_labels = sorted_y[:split_idx]
            right_labels = sorted_y[split_idx:]
            h_left = entropy(left_labels)
            h_right = entropy(right_labels)
            weighted_entropy = len_left / len_target * h_left + len_right / len_target * h_right
            current_ig = parent_entropy - weighted_entropy
        if current_ig > max_ig:
            max_ig = current_ig
            best_t = t
        elif current_ig == max_ig:
            if t < best_t:
                best_t = t
    if max_ig <= 0:
        return (None, 0.0)
    else:
        return (round(best_t, 4), round(max_ig, 4))

import numpy as np
def average_pool2d(X: np.ndarray, pool_shape: tuple[int, int], stride: tuple[int, int], accum_grad: np.ndarray) -> tuple[list, list]:
    """Performs forward and backward passes of a 2-D average-pooling layer."""
    (N, C, H, W) = X.shape
    (p_h, p_w) = pool_shape
    (s_h, s_w) = stride
    output_h = (H - p_h) // s_h + 1
    output_w = (W - p_w) // s_w + 1
    pooled_output = np.zeros((N, C, output_h, output_w))
    for n in range(N):
        for c in range(C):
            for i in range(output_h):
                h_start = i * s_h
                h_end = h_start + p_h
                for j in range(output_w):
                    w_start = j * s_w
                    w_end = w_start + p_w
                    window = X[n, c, h_start:h_end, w_start:w_end]
                    pooled_output[n, c, i, j] = window.mean()
    grad_input = np.zeros_like(X)
    for n in range(N):
        for c in range(C):
            for i in range(output_h):
                h_start = i * s_h
                h_end = h_start + p_h
                for j in range(output_w):
                    w_start = j * s_w
                    w_end = w_start + p_w
                    contribution = accum_grad[n, c, i, j] / (p_h * p_w)
                    grad_input[n, c, h_start:h_end, w_start:w_end] += contribution
    pooled_output_rounded = np.round(pooled_output, decimals=4)
    grad_input_rounded = np.round(grad_input, decimals=4)
    return (pooled_output_rounded.tolist(), grad_input_rounded.tolist())

import numpy as np
from dataclasses import dataclass
@dataclass
class Leaf:
    """A terminal node that stores a prediction value."""
    value: object
@dataclass
class Node:
    """An internal decision-tree node.

    Attributes:
        feature (int):   Index of the feature to test.
        threshold (float): Threshold that splits the data.
        left (Node | Leaf):  Sub-tree for samples with feature value < threshold.
        right (Node | Leaf): Sub-tree for samples with feature value ≥ threshold.
    """
    feature: int
    threshold: float
    left: object
    right: object
def compare_trees(tree_a, tree_b):
    """Recursively checks whether *tree_a* and *tree_b* are equivalent.

    Args:
        tree_a: Root node of the first decision tree (Node or Leaf).
        tree_b: Root node of the second decision tree (Node or Leaf).

    Returns:
        True if the two trees are equivalent, False otherwise.
    """
    if isinstance(tree_a, Leaf) and isinstance(tree_b, Leaf):
        return np.allclose(tree_a.value, tree_b.value, atol=1e-08, rtol=0)
    elif isinstance(tree_a, Node) and isinstance(tree_b, Node):
        if tree_a.feature != tree_b.feature:
            return False
        if not np.isclose(tree_a.threshold, tree_b.threshold, atol=1e-08, rtol=0):
            return False
        return compare_trees(tree_a.left, tree_b.left) and compare_trees(tree_a.right, tree_b.right)
    else:
        return False

import math
from collections import Counter
from typing import List
def ngram_log_prob(corpus: List[str], query: str, N: int) -> float:
    """Return the total log-probability of *query* under an N-gram MLE model.

    Parameters
    ----------
    corpus : list[str]
        A list of training sentences. Each sentence is a plain string; tokens
        are assumed to be separated by white-spaces.
    query : str
        The sentence whose probability should be calculated.
    N : int
        The order of the N-gram model (N ≥ 1).

    Returns
    -------
    float
        The sum of natural logarithms of the probabilities of every
        consecutive N-gram occurring in *query*, rounded to 4 decimal places.
        If any necessary N-gram is unseen in the corpus the function returns
        float('-inf').
    """
    if N == 1:
        all_tokens = []
        for sentence in corpus:
            all_tokens.extend(sentence.split())
        total_tokens = len(all_tokens)
        ngram_counts = Counter(all_tokens)
    else:
        ngram_list = []
        prefix_list = []
        for sentence in corpus:
            tokens = sentence.split()
            padded = ['<bol>'] * (N - 1) + tokens + ['<eol>']
            for i in range(len(padded) - N + 1):
                ngram = tuple(padded[i:i + N])
                ngram_list.append(ngram)
            for i in range(len(padded) - (N - 1) + 1):
                prefix = tuple(padded[i:i + (N - 1)])
                prefix_list.append(prefix)
        ngram_counts = Counter(ngram_list)
        prefix_counts = Counter(prefix_list)
    query_tokens = query.split()
    if N == 1:
        padded_query = query_tokens
    else:
        padded_query = ['<bol>'] * (N - 1) + query_tokens + ['<eol>']
    if len(padded_query) < N:
        return float('-inf')
    query_ngrams = [tuple(padded_query[i:i + N]) for i in range(len(padded_query) - N + 1)]
    if N == 1:
        if total_tokens == 0:
            return float('-inf')
        for ngram in query_ngrams:
            token = ngram[0]
            if ngram_counts.get(token, 0) == 0:
                return float('-inf')
    else:
        for ngram in query_ngrams:
            prefix = ngram[:-1]
            if prefix_counts.get(prefix, 0) == 0 or ngram_counts.get(ngram, 0) == 0:
                return float('-inf')
    log_prob = 0.0
    if N == 1:
        for ngram in query_ngrams:
            token = ngram[0]
            prob = ngram_counts[token] / total_tokens
            log_prob += math.log(prob)
    else:
        for ngram in query_ngrams:
            prefix = ngram[:-1]
            prob = ngram_counts[ngram] / prefix_counts[prefix]
            log_prob += math.log(prob)
    return round(log_prob, 4)

from collections import Counter
def build_fp_tree(transactions: list[list[str]], min_support: int) -> dict:
    """Construct an FP-Tree using only nested dictionaries.

    Parameters
    ----------
    transactions : list[list[str]]
        A list where each element is a list of items representing one transaction.
    min_support : int
        Minimum number of transactions an item has to appear in to be kept.

    Returns
    -------
    dict
        The root node of the FP-Tree.  Each node contains two keys:
        "support" and "children" (the latter mapping item → child-node).
    """
    item_supports = Counter()
    for transaction in transactions:
        unique_items = set(transaction)
        item_supports.update(unique_items)
    frequent_items = {item: cnt for (item, cnt) in item_supports.items() if cnt >= min_support}
    if not frequent_items:
        return {'support': 0, 'children': {}}
    sorted_frequent_items = sorted(frequent_items.keys(), key=lambda x: (-frequent_items[x], x))
    root = {'support': 0, 'children': {}}
    for transaction in transactions:
        unique_items = set(transaction)
        filtered_items = [item for item in unique_items if item in frequent_items]
        filtered_items_sorted = sorted(filtered_items, key=lambda x: sorted_frequent_items.index(x))
        current_node = root
        for item in filtered_items_sorted:
            if item in current_node['children']:
                current_node['children'][item]['support'] += 1
            else:
                current_node['children'][item] = {'support': 1, 'children': {}}
            current_node = current_node['children'][item]
    return root

import numpy as np
def linear_kernel(X: list[list[int | float]] | 'np.ndarray', Y: list[list[int | float]] | 'np.ndarray' | None=None, c0: int | float=0) -> list[list[float]] | int:
    """Return the linear kernel matrix k(x, y) = x·y + c0.

    Parameters
    ----------
    X : 2-D list or NumPy array with shape (N, C)
        Collection of N input vectors.
    Y : 2-D list or NumPy array with shape (M, C) or None, optional
        Collection of M input vectors.  If None, the function must set
        ``Y = X``.  Default is None.
    c0 : int or float, optional
        Additive constant of the kernel.  Default is 0.

    Returns
    -------
    list[list[float]] | int
        The (N, M) kernel matrix where entry (i, j) equals
        X[i]·Y[j] + c0, rounded to 4 decimals.  Return -1 when X and Y have
        incompatible shapes (different number of columns).
    """
    if Y is None:
        Y = X
    if isinstance(X, list):
        num_vectors_X = len(X)
    else:
        num_vectors_X = X.shape[0]
    if num_vectors_X == 0:
        return []
    try:
        x_arr = np.array(X, dtype=float)
        if x_arr.ndim == 1:
            x_arr = x_arr.reshape(1, -1)
    except:
        return -1
    try:
        y_arr = np.array(Y, dtype=float)
        if y_arr.ndim == 1:
            y_arr = y_arr.reshape(1, -1)
    except:
        return -1
    if x_arr.shape[1] != y_arr.shape[1]:
        return -1
    kernel_matrix = x_arr @ y_arr.T + c0
    return np.round(kernel_matrix, decimals=4).tolist()

import numpy as np
def kmeans(data: np.ndarray, K: int, max_iters: int=100) -> list[list[float]]:
    """Cluster *data* into *K* groups using the K–Means algorithm."""
    if K == 1:
        centroid = np.mean(data, axis=0)
        rounded_centroid = np.round(centroid, 4).tolist()
        return [rounded_centroid]
    centroids = data[:K].copy()
    for _ in range(max_iters):
        squared_distances = np.sum((data[:, np.newaxis] - centroids) ** 2, axis=2)
        cluster_assignments = np.argmin(squared_distances, axis=1)
        new_centroids = np.zeros_like(centroids)
        for k in range(K):
            cluster_points = data[cluster_assignments == k]
            if len(cluster_points) == 0:
                new_centroids[k] = centroids[k]
            else:
                new_centroids[k] = np.mean(cluster_points, axis=0)
        if np.max(np.abs(centroids - new_centroids)) < 1e-06:
            break
        centroids = new_centroids
    sorted_indices = np.lexsort(centroids.T[::-1])
    sorted_centroids = centroids[sorted_indices]
    rounded_centroids = np.round(sorted_centroids, 4)
    return rounded_centroids.tolist()

from typing import Any, Dict, Iterable, Tuple as PyTuple
class Space:
    pass
class Box(Space):

    def __init__(self, low: float, high: float, shape: PyTuple[int, ...]):
        self.low = low
        self.high = high
        self.shape = shape
class Tuple(Space):

    def __init__(self, spaces: Iterable[Space]):
        self.spaces = tuple(spaces)
class Dict(Space):

    def __init__(self, spaces: Dict[str, Space]):
        self.spaces = dict(spaces)
class Env:
    """Tiny environment that only stores two spaces."""

    def __init__(self, action_space: Space, observation_space: Space):
        self.action_space = action_space
        self.observation_space = observation_space
def is_continuous(env: Env, tuple_action: bool, tuple_obs: bool):
    """Determine whether the given environment's spaces are continuous.

    A space is *continuous* if it is an instance of `Box`. For composite spaces
    (`Tuple` or `Dict`) the space is continuous only if **all** its sub-spaces
    are `Box`.

    Args:
        env:          Environment exposing `action_space` and `observation_space`.
        tuple_action: Whether the *action* space is composite.
        tuple_obs:    Whether the *observation* space is composite.

    Returns:
        A tuple `(cont_action, cont_obs)` of booleans.
    """

    def check_space(space: Space, is_composite: bool) -> bool:
        if not is_composite:
            return isinstance(space, Box)
        else:
            if isinstance(space, Tuple):
                sub_spaces = space.spaces
            elif isinstance(space, Dict):
                sub_spaces = space.spaces.values()
            else:
                return False
            return all((isinstance(s, Box) for s in sub_spaces))
    cont_action = check_space(env.action_space, tuple_action)
    cont_obs = check_space(env.observation_space, tuple_obs)
    return (cont_action, cont_obs)

import numpy as np
def conv1D(X: np.ndarray, W: np.ndarray, stride: int, pad, dilation: int=0) -> list:
    """Perform a 1-D cross-correlation between *X* and *W*."""
    (n_examples, signal_length, in_channels) = X.shape
    (kernel_width, W_in_channels, out_channels) = W.shape
    if in_channels != W_in_channels:
        raise ValueError(f'Input channels mismatch: X has {in_channels}, W has {W_in_channels}')
    if stride <= 0:
        raise ValueError(f'Stride must be positive, got {stride}')
    if dilation < 0:
        raise ValueError(f'Dilation must be non-negative, got {dilation}')
    K = kernel_width
    D = dilation
    K_eff = (K - 1) * (D + 1) + 1 if K > 0 else 0
    if isinstance(pad, int):
        p_left = p_right = pad
    elif isinstance(pad, tuple):
        if len(pad) != 2 or not all((isinstance(p, int) for p in pad)):
            raise ValueError('Pad must be a 2-tuple of integers')
        (p_left, p_right) = pad
    elif pad == 'same':
        l_in = signal_length
        out_len_same = (l_in + stride - 1) // stride
        total_pad = max(0, (out_len_same - 1) * stride + K_eff - l_in)
        p_left = total_pad // 2
        p_right = total_pad - p_left
    else:
        raise ValueError('Invalid pad specification')
    X_padded = np.pad(X, pad_width=((0, 0), (p_left, p_right), (0, 0)), mode='constant')
    padded_length = signal_length + p_left + p_right
    output_length = (padded_length - K_eff) // stride + 1
    if output_length < 0:
        output_length = 0
    output = np.zeros((n_examples, output_length, out_channels), dtype=X.dtype)
    for n in range(n_examples):
        x_padded = X_padded[n]
        for s_idx in range(output_length):
            s = s_idx * stride
            x_slice = x_padded[s:s + K_eff:D + 1, :]
            output[n, s_idx, :] = np.einsum('ki, kio -> o', x_slice, W)
    return output.tolist()

import numpy as np
def is_symmetric(X: list[list[int | float]]) -> bool:
    """Check whether the given 2-D array *X* is symmetric.

    A matrix is symmetric if it is square and equal to its own transpose.

    Args:
        X: Matrix represented as a list of lists containing numbers.

    Returns:
        True if the matrix is symmetric, otherwise False.
    """
    if not X:
        return True
    num_rows = len(X)
    num_cols = len(X[0])
    for row in X:
        if len(row) != num_cols:
            return False
    if num_rows != num_cols:
        return False
    arr = np.array(X)
    return np.allclose(arr, arr.T, atol=1e-08, rtol=0.0)

import numpy as np
def env_stats(actions: list, observations: list) -> dict:
    """Compute statistics for collections of actions and observations."""

    def process_list(lst):
        tuple_flag = any((isinstance(x, (list, tuple, np.ndarray)) for x in lst))
        first_elem = lst[0]
        if isinstance(first_elem, (list, tuple, np.ndarray)):
            dim = len(first_elem)
        else:
            dim = 1
        multidim_flag = dim > 1
        per_dim_values = []
        for i in range(dim):
            dim_values = []
            for elem in lst:
                if isinstance(elem, (list, tuple, np.ndarray)):
                    val = elem[i]
                else:
                    val = elem if i == 0 else None
                dim_values.append(val)
            per_dim_values.append(dim_values)
        n_per_dim = []
        ids_list = []
        for dim_vals in per_dim_values:
            unique_vals = sorted(set(dim_vals))
            ids_list.append(unique_vals)
            n_per_dim.append(len(unique_vals))
        continuous = False
        for dim_vals in per_dim_values:
            for val in dim_vals:
                if isinstance(val, float) and val != int(val):
                    continuous = True
                    break
                if isinstance(val, np.floating) and val != val.astype(int):
                    continuous = True
                    break
            if continuous:
                break
        return (tuple_flag, multidim_flag, continuous, n_per_dim, dim, ids_list)
    (tuple_actions, multidim_actions, continuous_actions, n_actions_per_dim, action_dim, action_ids) = process_list(actions)
    (tuple_observations, multidim_observations, continuous_observations, n_obs_per_dim, obs_dim, obs_ids) = process_list(observations)
    return {'tuple_actions': tuple_actions, 'tuple_observations': tuple_observations, 'multidim_actions': multidim_actions, 'multidim_observations': multidim_observations, 'continuous_actions': continuous_actions, 'continuous_observations': continuous_observations, 'n_actions_per_dim': n_actions_per_dim, 'n_obs_per_dim': n_obs_per_dim, 'action_dim': action_dim, 'obs_dim': obs_dim, 'action_ids': action_ids, 'obs_ids': obs_ids}

import numpy as np
def gbdt_regression(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, n_estimators: int=10, learning_rate: float=0.1, max_depth: int=3) -> list[float]:
    """Gradient Boosting Decision Tree (GBDT) regressor."""

    class Node:

        def __init__(self, is_leaf, value=None, feature=None, threshold=None, left=None, right=None):
            self.is_leaf = is_leaf
            self.value = value
            self.feature = feature
            self.threshold = threshold
            self.left = left
            self.right = right

    def find_best_split(X, y):
        (n_samples, n_features) = X.shape
        if n_samples <= 1:
            return (None, None)
        current_mse = np.var(y) * n_samples
        best_mse = float('inf')
        best_feature = None
        best_threshold = None
        for feature in range(n_features):
            x_vals = X[:, feature]
            unique_vals = np.unique(x_vals)
            for threshold in unique_vals:
                left_mask = x_vals <= threshold
                right_mask = x_vals > threshold
                n_left = np.sum(left_mask)
                n_right = np.sum(right_mask)
                if n_left == 0 or n_right == 0:
                    continue
                y_left = y[left_mask]
                y_right = y[right_mask]
                mse_left = np.var(y_left) * n_left
                mse_right = np.var(y_right) * n_right
                total_mse = mse_left + mse_right
                if total_mse < best_mse:
                    best_mse = total_mse
                    best_feature = feature
                    best_threshold = threshold
        if best_mse < current_mse:
            return (best_feature, best_threshold)
        else:
            return (None, None)

    def build_tree(X, y, current_depth):
        if current_depth >= max_depth:
            return Node(is_leaf=True, value=np.mean(y))
        if len(np.unique(y)) == 1:
            return Node(is_leaf=True, value=np.mean(y))
        (best_feature, best_threshold) = find_best_split(X, y)
        if best_feature is None:
            return Node(is_leaf=True, value=np.mean(y))
        x_feature = X[:, best_feature]
        left_mask = x_feature <= best_threshold
        right_mask = x_feature > best_threshold
        if np.sum(left_mask) == 0 or np.sum(right_mask) == 0:
            return Node(is_leaf=True, value=np.mean(y))
        left_subtree = build_tree(X[left_mask], y[left_mask], current_depth + 1)
        right_subtree = build_tree(X[right_mask], y[right_mask], current_depth + 1)
        return Node(is_leaf=False, feature=best_feature, threshold=best_threshold, left=left_subtree, right=right_subtree)

    def predict_tree(X, tree):
        predictions = np.zeros(X.shape[0])
        for i in range(X.shape[0]):
            sample = X[i]
            node = tree
            while not node.is_leaf:
                if sample[node.feature] <= node.threshold:
                    node = node.left
                else:
                    node = node.right
            predictions[i] = node.value
        return predictions
    initial_pred = np.mean(y_train)
    current_pred_train = np.full_like(y_train, initial_pred)
    trees = []
    for _ in range(n_estimators):
        residuals = y_train - current_pred_train
        tree = build_tree(X_train, residuals, current_depth=0)
        trees.append(tree)
        tree_pred_train = predict_tree(X_train, tree)
        current_pred_train += learning_rate * tree_pred_train
    test_pred = np.full(X_test.shape[0], initial_pred)
    for tree in trees:
        test_pred += learning_rate * predict_tree(X_test, tree)
    return [round(x, 4) for x in test_pred.tolist()]

import numpy as np
def bayesian_linear_regression_pred(X: np.ndarray, y: np.ndarray, X_new: np.ndarray, mu: float | np.ndarray=0.0, sigma: float=1.0, V: float | np.ndarray | None=None, fit_intercept: bool=True) -> list[float]:
    """Bayesian linear regression with known variance.

    The function must return the MAP predictive means for every row in
    `X_new`.  See the task description for full details.
    """
    if fit_intercept:
        X_augmented = np.hstack([np.ones((X.shape[0], 1)), X])
        X_new_augmented = np.hstack([np.ones((X_new.shape[0], 1)), X_new])
    else:
        X_augmented = X.copy()
        X_new_augmented = X_new.copy()
    D = X_augmented.shape[1]
    if isinstance(mu, (float, int)):
        mu_vec = np.full(D, mu, dtype=np.float64)
    else:
        mu_vec = np.asarray(mu, dtype=np.float64)
        if mu_vec.shape != (D,):
            raise ValueError(f'mu must be a scalar or a vector of length {D}')
    if V is None:
        V_matrix = np.eye(D, dtype=np.float64)
    else:
        V_arr = np.asarray(V)
        if V_arr.ndim == 0:
            V_matrix = V_arr.item() * np.eye(D, dtype=np.float64)
        elif V_arr.ndim == 1:
            if len(V_arr) != D:
                raise ValueError(f'1D V must have length {D}, got {len(V_arr)}')
            V_matrix = np.diag(V_arr).astype(np.float64)
        elif V_arr.ndim == 2:
            if V_arr.shape != (D, D):
                raise ValueError(f'2D V must have shape ({D}, {D}), got {V_arr.shape}')
            V_matrix = V_arr.astype(np.float64)
        else:
            raise ValueError('V must be None, scalar, 1D, or 2D array')
    V_inv = np.linalg.inv(V_matrix)
    XTX = X_augmented.T @ X_augmented
    XTy = X_augmented.T @ y
    A = V_inv + XTX
    Sigma_p = np.linalg.inv(A)
    mu_p = Sigma_p @ (V_inv @ mu_vec + XTy)
    predictions = X_new_augmented @ mu_p
    return [round(pred, 4) for pred in predictions.tolist()]

import numpy as np
def root_mean_squared_log_error(actual: list[float], predicted: list[float]) -> float:
    """Calculates the Root Mean Squared Logarithmic Error (RMSLE) between actual and predicted values."""
    if len(actual) != len(predicted):
        return -1
    n = len(actual)
    if n == 0:
        return -1
    if not (all((a >= 0 for a in actual)) and all((p >= 0 for p in predicted))):
        return -1
    log_actual = np.log1p(actual)
    log_predicted = np.log1p(predicted)
    squared_diffs = (log_predicted - log_actual) ** 2
    mean_squared_log_error = np.mean(squared_diffs)
    rmsle = np.sqrt(mean_squared_log_error)
    return round(rmsle, 4)

import numpy as np
def sigmoid(x, order: int=0):
    """Compute the logistic sigmoid or its derivatives.

    Args:
        x (float | int | list | np.ndarray): Input data. Can be a scalar, list, or NumPy array.
        order (int, optional): 0 = function value, 1 = first derivative,
            2 = second derivative. Defaults to 0.

    Returns:
        float | list: Result rounded to 4 decimals. Scalar input returns a float;
            vector/matrix input returns a Python list preserving the shape.
            If *order* is not 0, 1, or 2, the function returns -1.
    """
    if order not in {0, 1, 2}:
        return -1
    x_arr = np.asarray(x)
    sigma = 1 / (1 + np.exp(-x_arr))
    if order == 0:
        res = sigma
    elif order == 1:
        res = sigma * (1 - sigma)
    else:
        res = sigma * (1 - sigma) * (1 - 2 * sigma)
    rounded = np.round(res, 4)
    if isinstance(x, (int, float)):
        return float(rounded.item())
    else:
        return rounded.tolist()

import numpy as np
def rbf_kernel(X: list[list[int | float]], Y: list[list[int | float]] | None=None, sigma: float | list[float] | None=None) -> list[list[float]]:
    """Compute the Radial Basis Function (RBF) kernel matrix.

    The function should follow the specifications given in the task
    description. It must return -1 on invalid input, otherwise a nested list
    containing the kernel matrix rounded to four decimal places.
    """
    if Y is None:
        Y = X
    N = len(X)
    if N == 0:
        C = 0
    else:
        C = len(X[0])
        for x in X:
            if len(x) != C:
                return -1
    M = len(Y)
    if M == 0:
        D = 0
    else:
        D = len(Y[0])
        for y in Y:
            if len(y) != D:
                return -1
    if C != D:
        return -1
    if sigma is None:
        if C == 0:
            return -1
        sigma_val = np.sqrt(C / 2)
        sigma_array = np.full(C, sigma_val, dtype=float)
    elif isinstance(sigma, list):
        if len(sigma) != C:
            return -1
        for s in sigma:
            if not (isinstance(s, (int, float)) and s > 0):
                return -1
        sigma_array = np.array(sigma, dtype=float)
    else:
        if not (isinstance(sigma, (int, float)) and sigma > 0):
            return -1
        sigma_array = np.full(C, sigma, dtype=float)
    X_np = np.array(X, dtype=float)
    Y_np = np.array(Y, dtype=float)
    X_scaled = X_np / sigma_array
    Y_scaled = Y_np / sigma_array
    X_sq = (X_scaled ** 2).sum(axis=1)
    Y_sq = (Y_scaled ** 2).sum(axis=1)
    dist_sq = X_sq[:, np.newaxis] + Y_sq - 2 * (X_scaled @ Y_scaled.T)
    kernel_matrix = np.exp(-0.5 * dist_sq)
    kernel_rounded = np.round(kernel_matrix, 4)
    return kernel_rounded.tolist()

import numpy as np
def decision_stump(X: list[list[float]], y: list[int]) -> tuple:
    """Find a decision stump that maximises information gain."""
    X = np.array(X, dtype=float)
    y = np.array(y, dtype=int)
    (n_samples, n_features) = X.shape

    def entropy(labels):
        if len(labels) == 0:
            return 0.0
        counts = np.bincount(labels)
        probs = counts / len(labels)
        probs = probs[probs > 0]
        return -np.sum(probs * np.log2(probs))

    def majority_label(labels):
        if len(labels) == 0:
            return None
        counts = np.bincount(labels)
        max_count = np.max(counts)
        candidates = np.where(counts == max_count)[0]
        return np.min(candidates)
    H_parent = entropy(y)
    if H_parent == 0:
        maj = majority_label(y)
        return (-1, None, maj, maj)
    max_gain = -np.inf
    best_j = -1
    best_t = None
    best_left_maj = None
    best_right_maj = None
    for j in range(n_features):
        X_j = X[:, j]
        unique_vals = np.unique(X_j)
        if len(unique_vals) < 2:
            continue
        midpoints = (unique_vals[:-1] + unique_vals[1:]) / 2
        for t in midpoints:
            mask_left = X_j <= t
            left_labels = y[mask_left]
            right_labels = y[~mask_left]
            if len(left_labels) == 0 or len(right_labels) == 0:
                continue
            H_left = entropy(left_labels)
            H_right = entropy(right_labels)
            p_left = len(left_labels) / n_samples
            p_right = len(right_labels) / n_samples
            gain = H_parent - (p_left * H_left + p_right * H_right)
            left_maj = majority_label(left_labels)
            right_maj = majority_label(right_labels)
            if gain > max_gain:
                max_gain = gain
                best_j = j
                best_t = t
                best_left_maj = left_maj
                best_right_maj = right_maj
            elif gain == max_gain:
                if j < best_j:
                    best_j = j
                    best_t = t
                    best_left_maj = left_maj
                    best_right_maj = right_maj
                elif j == best_j:
                    if t < best_t:
                        best_t = t
                        best_left_maj = left_maj
                        best_right_maj = right_maj
    if max_gain <= 0:
        maj = majority_label(y)
        return (-1, None, maj, maj)
    else:
        best_t_rounded = round(best_t, 4)
        return (best_j, best_t_rounded, best_left_maj, best_right_maj)

import numpy as np
def update_q_values(Q: 'np.ndarray', Q_next: 'np.ndarray', actions: list[int], rewards: list[float], dones: list[bool], gamma: float) -> list[list[float]]:
    """Fill in DQN targets for a training batch."""
    if not 0 <= gamma <= 1:
        return -1
    if Q.ndim != 2 or Q_next.ndim != 2 or Q.shape != Q_next.shape:
        return -1
    (batch_size, n_actions) = Q.shape
    if len(actions) != batch_size or len(rewards) != batch_size or len(dones) != batch_size:
        return -1
    for action in actions:
        if action < 0 or action >= n_actions:
            return -1
    y = Q.copy()
    for i in range(batch_size):
        if dones[i]:
            target = rewards[i]
        else:
            next_max = np.max(Q_next[i])
            target = rewards[i] + gamma * next_max
        y[i, actions[i]] = target
    y_rounded = np.round(y, decimals=4)
    return y_rounded.tolist()

import numpy as np
def regression_tree_predict(X_train: list[float | int], y_train: list[float | int], X_test: list[float | int], max_depth: int=3) -> list[float]:
    """Fit a univariate regression tree of depth *max_depth* and predict."""
    if not X_train:
        return []
    sorted_pairs = sorted(zip(X_train, y_train), key=lambda p: p[0])
    sorted_X = [p[0] for p in sorted_pairs]
    sorted_y = [p[1] for p in sorted_pairs]

    def build_tree(X: list[float], y: list[float], current_depth: int) -> dict:
        """Recursively builds the regression tree."""
        if current_depth == max_depth or len(X) < 2:
            return {'prediction': np.mean(y)}
        best_sse = float('inf')
        best_split_val = None
        (best_left_X, best_left_y) = ([], [])
        (best_right_X, best_right_y) = ([], [])
        for i in range(len(X) - 1):
            split_val = (X[i] + X[i + 1]) / 2
            (left_X, left_y) = (X[:i + 1], y[:i + 1])
            (right_X, right_y) = (X[i + 1:], y[i + 1:])
            if not left_y or not right_y:
                continue
            mean_left = np.mean(left_y)
            sse_left = sum(((yy - mean_left) ** 2 for yy in left_y))
            mean_right = np.mean(right_y)
            sse_right = sum(((yy - mean_right) ** 2 for yy in right_y))
            total_sse = sse_left + sse_right
            if total_sse < best_sse:
                best_sse = total_sse
                best_split_val = split_val
                (best_left_X, best_left_y) = (left_X, left_y)
                (best_right_X, best_right_y) = (right_X, right_y)
        if best_split_val is None:
            return {'prediction': np.mean(y)}
        left_subtree = build_tree(best_left_X, best_left_y, current_depth + 1)
        right_subtree = build_tree(best_right_X, best_right_y, current_depth + 1)
        return {'split_val': best_split_val, 'left': left_subtree, 'right': right_subtree}
    root = build_tree(sorted_X, sorted_y, 0)

    def predict(x: float) -> float:
        node = root
        while 'prediction' not in node:
            if x <= node['split_val']:
                node = node['left']
            else:
                node = node['right']
        return node['prediction']
    return [predict(x) for x in X_test]

import numpy as np
from itertools import combinations_with_replacement
def polynomial_features(X: list[list[int | float]], degree: int) -> list[list[float]]:
    """Generate a full polynomial feature matrix up to a given degree."""
    if degree < 0:
        return -1
    if not X:
        return []
    m = len(X[0]) if X else 0
    result = []
    for sample in X:
        row = [1.0]
        for k in range(1, degree + 1):
            for indices in combinations_with_replacement(range(m), k):
                product = 1.0
                for idx in indices:
                    product *= sample[idx]
                row.append(float(product))
        result.append(row)
    return result

import numpy as np
def sigmoid_grad(z):
    """Return the derivative of the sigmoid function given its output.

    The input *z* already contains sigmoid values (numbers in [0,1]).  The
    derivative is computed as z*(1-z) element-wise.

    Args:
        z: float, list or NumPy ndarray of sigmoid outputs.

    Returns:
        Same structure as *z* (float or nested list) with each value replaced
        by its gradient, rounded to 4 decimal places.
    """

    def process_list(lst):
        processed = []
        for element in lst:
            if isinstance(element, list):
                processed.append(process_list(element))
            else:
                val = element * (1 - element)
                rounded = np.round(val, 4)
                processed.append(float(rounded))
        return processed
    if isinstance(z, np.ndarray):
        if z.ndim == 0:
            val = z.item()
            result = val * (1 - val)
            return np.round(result, 4).item()
        else:
            list_z = z.tolist()
            return process_list(list_z)
    elif isinstance(z, list):
        if not z:
            return []
        return process_list(z)
    else:
        result = z * (1 - z)
        return np.round(result, 4).item()

import numpy as np
def spectral_clustering(data: np.ndarray, n_cluster: int, gamma: float=2.0, method: str='unnormalized') -> list[int]:
    """Perform spectral clustering on the given dataset."""
    N = data.shape[0]
    if n_cluster == 1:
        return [0] * N
    pairwise_sq_dists = np.sum(data ** 2, axis=1)[:, np.newaxis] + np.sum(data ** 2, axis=1) - 2 * np.dot(data, data.T)
    W = np.exp(-gamma * pairwise_sq_dists)
    D = np.sum(W, axis=1)
    if method == 'unnormalized':
        L = np.diag(D) - W
    else:
        D_sqrt = np.sqrt(D)
        L = (np.diag(D) - W) / (D_sqrt[:, np.newaxis] * D_sqrt)
    (eigenvalues, eigenvectors) = np.linalg.eigh(L)
    U = eigenvectors[:, :n_cluster]
    if method == 'normalized':
        row_norms = np.linalg.norm(U, axis=1)
        row_norms = np.where(row_norms == 0, 1e-10, row_norms)
        U = U / row_norms[:, np.newaxis]

    def kmeans(X, n_clusters, initial_centroids):
        centroids = initial_centroids.copy()
        while True:
            distances = np.linalg.norm(X[:, np.newaxis] - centroids, axis=2)
            labels = np.argmin(distances, axis=1)
            new_centroids = np.zeros_like(centroids)
            for i in range(n_clusters):
                mask = labels == i
                if np.sum(mask) == 0:
                    new_centroids[i] = initial_centroids[i]
                else:
                    new_centroids[i] = np.mean(X[mask], axis=0)
            if np.allclose(centroids, new_centroids):
                break
            centroids = new_centroids
        return labels
    initial_centroids = U[:n_cluster, :]
    kmeans_labels = kmeans(U, n_cluster, initial_centroids)
    relabel_mapping = {}
    current_label = 0
    for idx in range(N):
        old_label = kmeans_labels[idx]
        if old_label not in relabel_mapping:
            relabel_mapping[old_label] = current_label
            current_label += 1
    return [relabel_mapping[old] for old in kmeans_labels]

def predict_sample(root: 'Node', features: list | tuple, task_type: str):
    """Return the prediction for one sample by traversing the decision tree.

    Args:
        root (Node): The root node of the decision tree.
        features (list | tuple): The feature values of the sample.
        task_type (str): Either 'classification' or 'regression'.

    Returns:
        The value stored in the reached leaf node.
    """
    if hasattr(root, 'y'):
        return root.y
    if task_type == 'classification':
        feature_idx = root.label
        feature_val = features[feature_idx]
        for child in root.child:
            if child.x == feature_val:
                return predict_sample(child, features, task_type)
        return predict_sample(root.child[1], features, task_type)
    else:
        feature_idx = root.label
        feature_val = features[feature_idx]
        if feature_val <= root.s:
            return predict_sample(root.child[0], features, task_type)
        else:
            return predict_sample(root.child[1], features, task_type)

import numpy as np
import math
def detect_outliers(data: np.ndarray, n_trees: int, sample_size: int, epsilon: float) -> list[int]:
    """Tiny Isolation Forest implementation."""
    if epsilon == 0.0:
        return []
    (n_samples, n_features) = data.shape
    if n_samples == 0:
        return []
    np.random.seed(42)
    s = min(sample_size, n_samples)
    if s <= 1:
        phi = 0.0
    else:
        phi = 2 * math.log(s - 1) - 2 * (s - 1) / s
    height_limit = math.ceil(math.log2(s)) if s > 0 else 0

    def build_tree(subsample_data):

        def build_node(current_data, current_depth):
            if current_depth >= height_limit or len(current_data) <= 1:
                return {'type': 'leaf', 'size': len(current_data)}
            feature = np.random.randint(0, n_features)
            min_val = np.min(current_data[:, feature])
            max_val = np.max(current_data[:, feature])
            if min_val == max_val:
                return {'type': 'leaf', 'size': len(current_data)}
            threshold = np.random.uniform(min_val, max_val)
            mask_left = current_data[:, feature] <= threshold
            left_data = current_data[mask_left]
            right_data = current_data[~mask_left]
            left_child = build_node(left_data, current_depth + 1)
            right_child = build_node(right_data, current_depth + 1)
            return {'type': 'internal', 'feature': feature, 'threshold': threshold, 'left': left_child, 'right': right_child}
        return build_node(subsample_data, 0)

    def compute_path(x, tree):

        def traverse(node, depth):
            if node['type'] == 'leaf':
                n = node['size']
                if n == 1:
                    return depth
                else:
                    return depth + 2 * math.log(n - 1) + 0.5772156649 - 2 * (n - 1) / n
            elif x[node['feature']] <= node['threshold']:
                return traverse(node['left'], depth + 1)
            else:
                return traverse(node['right'], depth + 1)
        return traverse(tree, 0)
    path_lengths = np.zeros((n_samples, n_trees))
    for tree_idx in range(n_trees):
        if s == n_samples:
            subsample_indices = np.arange(n_samples)
        else:
            subsample_indices = np.random.choice(n_samples, size=s, replace=False)
        subsample_data = data[subsample_indices]
        tree = build_tree(subsample_data)
        for i in range(n_samples):
            x = data[i]
            path_lengths[i, tree_idx] = compute_path(x, tree)
    h_bar = np.mean(path_lengths, axis=1)
    if phi == 0:
        scores = np.ones(n_samples)
    else:
        scores = 2 ** (-h_bar / phi)
    n_outliers = math.ceil(epsilon * n_samples)
    if n_outliers <= 0:
        return []
    indexed_scores = sorted(enumerate(scores), key=lambda x: (-x[1], x[0]))
    outliers = [idx for (idx, _) in indexed_scores[:n_outliers]]
    outliers.sort()
    return outliers
