from typing import List, Dict, Tuple, Hashable
def find_frequent_itemsets(transactions: List[List[Hashable]], minsup: float) -> Dict[Tuple[Hashable, ...], int]:
    """Return all frequent itemsets in **transactions**.

    Args:
        transactions: List where each element represents a transaction and is
                       itself a list of items (hashable Python objects).
        minsup:       Minimum support given as a fraction between 0 and 1.

    Returns:
        A dictionary that maps every frequent itemset (stored as a tuple of
        sorted items) to its absolute support count.  If no itemset reaches
        the threshold the function must return an empty dictionary.
    """
    num_transactions = len(transactions)
    min_support_count = math.ceil(minsup * num_transactions)
    frequent_itemsets: Dict[Tuple[Hashable, ...], int] = {}
    item_counts = Counter()
    for transaction in transactions:
        for item in set(transaction):
            item_counts[item] += 1
    frequent_items = {item for (item, count) in item_counts.items() if count >= min_support_count}
    if not frequent_items:
        return {}
    for item in sorted(list(frequent_items)):
        frequent_itemsets[item,] = item_counts[item]
    current_frequent_itemsets = {frozenset(itemset) for itemset in frequent_itemsets.keys()}
    k = 2
    while current_frequent_itemsets:
        candidate_itemsets = set()
        for itemset1 in current_frequent_itemsets:
            for itemset2 in current_frequent_itemsets:
                union_set = itemset1.union(itemset2)
                if len(union_set) == k:
                    is_valid_candidate = True
                    for subset in combinations(union_set, k - 1):
                        if frozenset(subset) not in current_frequent_itemsets:
                            is_valid_candidate = False
                            break
                    if is_valid_candidate:
                        candidate_itemsets.add(union_set)
        if not candidate_itemsets:
            break
        candidate_counts = Counter()
        for transaction in transactions:
            transaction_set = set(transaction)
            for candidate in candidate_itemsets:
                if candidate.issubset(transaction_set):
                    candidate_counts[candidate] += 1
        next_frequent_itemsets = set()
        for (itemset, count) in candidate_counts.items():
            if count >= min_support_count:
                sorted_itemset = tuple(sorted(list(itemset)))
                frequent_itemsets[sorted_itemset] = count
                next_frequent_itemsets.add(itemset)
        current_frequent_itemsets = next_frequent_itemsets
        k += 1
    return frequent_itemsets

