def build_fp_tree(transactions, min_support):
    """Builds the FP-tree from the transactions."""
    item_counts = Counter((item for transaction in transactions for item in transaction))
    frequent_items = {item for (item, count) in item_counts.items() if count >= min_support}
    if not frequent_items:
        return (None, None)
    header_table = defaultdict(lambda : None)
    root = FPTreeNode(None, 0, None)
    for transaction in transactions:
        ordered_transaction = [item for item in transaction if item in frequent_items]
        ordered_transaction.sort(key=lambda x: item_counts[x], reverse=True)
        current_node = root
        for item in ordered_transaction:
            if item not in current_node.children:
                new_node = FPTreeNode(item, 0, current_node)
                current_node.children[item] = new_node
                if header_table[item] is None:
                    header_table[item] = new_node
                else:
                    current = header_table[item]
                    while current.next_node:
                        current = current.next_node
                    current.next_node = new_node
            current_node.children[item].count += 1
            current_node = current_node.children[item]
    return (root, header_table)
