import numpy as np
def adaboost_predict(X_train: list[list[int | float]], y_train: list[int], X_test: list[list[int | float]], n_estimators: int=10) -> list[int]:
    """
    Implements the AdaBoost ensemble algorithm with decision stumps.

    Args:
        X_train: Training feature matrix.
        y_train: Training label vector (0 or 1).
        X_test: Test feature matrix.
        n_estimators: Number of boosting rounds.

    Returns:
        A list of predicted class labels (0 or 1) for X_test.
    """
    X_train = np.array(X_train)
    y_train = np.array(y_train)
    X_test = np.array(X_test)
    (n_samples, n_features) = X_train.shape
    weights = np.full(n_samples, 1 / n_samples)
    stumps = []
    alphas = []
    y_train_transformed = np.where(y_train == 0, -1, 1)
    for _ in range(n_estimators):
        best_stump = None
        min_weighted_error = float('inf')
        for feature_index in range(n_features):
            thresholds = np.unique(X_train[:, feature_index])
            for threshold in thresholds:
                for polarity in [1, -1]:
                    predictions = np.ones(n_samples)
                    if polarity == 1:
                        predictions[X_train[:, feature_index] < threshold] = -1
                    else:
                        predictions[X_train[:, feature_index] >= threshold] = -1
                    weighted_error = np.sum(weights * (predictions != y_train_transformed))
                    if weighted_error < min_weighted_error:
                        min_weighted_error = weighted_error
                        best_stump = {'feature_index': feature_index, 'threshold': threshold, 'polarity': polarity}
                    elif weighted_error == min_weighted_error:
                        if feature_index < best_stump['feature_index']:
                            best_stump = {'feature_index': feature_index, 'threshold': threshold, 'polarity': polarity}
                        elif feature_index == best_stump['feature_index']:
                            if threshold < best_stump['threshold']:
                                best_stump = {'feature_index': feature_index, 'threshold': threshold, 'polarity': polarity}
                            elif threshold == best_stump['threshold']:
                                if polarity == 1 and best_stump['polarity'] == -1:
                                    best_stump = {'feature_index': feature_index, 'threshold': threshold, 'polarity': polarity}
        if best_stump is None:
            break
        if min_weighted_error == 0:
            stumps.append(best_stump)
            alphas.append(float('inf'))
            break
        epsilon = 1e-10
        alpha = 0.5 * np.log((1.0 - min_weighted_error) / (min_weighted_error + epsilon))
        predictions = np.ones(n_samples)
        if best_stump['polarity'] == 1:
            predictions[X_train[:, best_stump['feature_index']] < best_stump['threshold']] = -1
        else:
            predictions[X_train[:, best_stump['feature_index']] >= best_stump['threshold']] = -1
        weights *= np.exp(-alpha * y_train_transformed * predictions)
        weights /= np.sum(weights)
        stumps.append(best_stump)
        alphas.append(alpha)
    final_predictions = np.zeros(X_test.shape[0])
    for (alpha, stump) in zip(alphas, stumps):
        stump_predictions = np.ones(X_test.shape[0])
        if stump['polarity'] == 1:
            stump_predictions[X_test[:, stump['feature_index']] < stump['threshold']] = -1
        else:
            stump_predictions[X_test[:, stump['feature_index']] >= stump['threshold']] = -1
        final_predictions += alpha * stump_predictions
    predicted_labels = np.where(final_predictions >= 0, 1, 0)
    return predicted_labels.tolist()

import numpy as np
def decision_tree_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, max_depth: int | None=None) -> list[int]:
    """Build a CART decision tree on (X_train, y_train) and predict labels for X_test.

    Parameters
    ----------
    X_train : np.ndarray
        Training feature matrix of shape (n_samples, n_features).
    y_train : np.ndarray
        Integer class labels for the training data, shape (n_samples,).
    X_test : np.ndarray
        Feature matrix to classify, shape (m_samples, n_features).
    max_depth : int | None, optional
        Maximum allowed depth of the tree. If None the depth is unlimited.

    Returns
    -------
    list[int]
        Predicted class label for each row in X_test.
    """

    class Node:

        def __init__(self, feature_index=None, threshold=None, left=None, right=None, value=None):
            self.feature_index = feature_index
            self.threshold = threshold
            self.left = left
            self.right = right
            self.value = value

    def gini_impurity(y):
        if len(y) == 0:
            return 0
        class_counts = np.bincount(y)
        probabilities = class_counts / len(y)
        return 1.0 - np.sum(probabilities ** 2)

    def find_best_split(X, y):
        (n_samples, n_features) = X.shape
        if n_samples <= 1:
            return (None, None, None)
        parent_gini = gini_impurity(y)
        best_gini_gain = 0
        best_feature_index = None
        best_threshold = None
        for feature_index in range(n_features):
            unique_values = np.unique(X[:, feature_index])
            if len(unique_values) <= 1:
                continue
            sorted_indices = np.argsort(X[:, feature_index])
            sorted_X = X[sorted_indices]
            sorted_y = y[sorted_indices]
            for i in range(len(unique_values) - 1):
                threshold = (unique_values[i] + unique_values[i + 1]) / 2.0
                left_indices = np.where(sorted_X[:, feature_index] <= threshold)[0]
                right_indices = np.where(sorted_X[:, feature_index] > threshold)[0]
                if len(left_indices) == 0 or len(right_indices) == 0:
                    continue
                y_left = sorted_y[left_indices]
                y_right = sorted_y[right_indices]
                gini_left = gini_impurity(y_left)
                gini_right = gini_impurity(y_right)
                weighted_gini = len(y_left) / n_samples * gini_left + len(y_right) / n_samples * gini_right
                gini_gain = parent_gini - weighted_gini
                if gini_gain > best_gini_gain:
                    best_gini_gain = gini_gain
                    best_feature_index = feature_index
                    best_threshold = threshold
        return (best_feature_index, best_threshold, best_gini_gain)

    def build_tree(X, y, depth):
        n_samples = len(y)
        unique_labels = np.unique(y)
        if len(unique_labels) == 1:
            return Node(value=unique_labels[0])
        if max_depth is not None and depth >= max_depth:
            return Node(value=np.argmax(np.bincount(y)))
        (feature_index, threshold, gini_gain) = find_best_split(X, y)
        if gini_gain <= 0 or feature_index is None:
            return Node(value=np.argmax(np.bincount(y)))
        left_indices = np.where(X[:, feature_index] <= threshold)[0]
        right_indices = np.where(X[:, feature_index] > threshold)[0]
        if len(left_indices) == 0 or len(right_indices) == 0:
            return Node(value=np.argmax(np.bincount(y)))
        (X_left, y_left) = (X[left_indices], y[left_indices])
        (X_right, y_right) = (X[right_indices], y[right_indices])
        left_subtree = build_tree(X_left, y_left, depth + 1)
        right_subtree = build_tree(X_right, y_right, depth + 1)
        return Node(feature_index=feature_index, threshold=threshold, left=left_subtree, right=right_subtree)

    def predict_single(node, x):
        if node.value is not None:
            return node.value
        if x[node.feature_index] <= node.threshold:
            return predict_single(node.left, x)
        else:
            return predict_single(node.right, x)
    root = build_tree(X_train, y_train, depth=0)
    predictions = []
    for x in X_test:
        predictions.append(predict_single(root, x))
    return predictions

import numpy as np
def metric(name: str):
    """Factory producing a validated distance function.

    The function creates and returns a callable *d* that computes one of four
    classical distances (Euclidean, Manhattan, Chebyshev, Cosine) between two
    numeric vectors.  All numeric outputs are rounded to four decimal places.

    Validation rules inside the returned callable:
    * Both arguments must be one-dimensional, non-empty, equal-length numeric
      iterables (list, tuple or NumPy array).
    * Metric *name* must be one of the supported strings.
    * For the cosine distance, zero-norm vectors are rejected.

    If the metric name is unsupported or validation fails, *d* returns -1.

    Args:
        name (str): Name of the desired metric.

    Returns:
        Callable[[Iterable, Iterable], float | int]: A distance function with
        integrated validation.
    """
    supported_metrics = ['euclidean', 'manhattan', 'chebyshev', 'cosine']
    if name not in supported_metrics:
        return lambda x, y: -1

    def validate_vectors(x, y):
        if not isinstance(x, (list, tuple, np.ndarray)) or not isinstance(y, (list, tuple, np.ndarray)):
            return False
        try:
            x_arr = np.asarray(x, dtype=float)
            y_arr = np.asarray(y, dtype=float)
        except ValueError:
            return False
        if x_arr.ndim != 1 or y_arr.ndim != 1:
            return False
        if len(x_arr) == 0 or len(y_arr) == 0:
            return False
        if len(x_arr) != len(y_arr):
            return False
        return (x_arr, y_arr)

    def distance_func(x, y):
        validated = validate_vectors(x, y)
        if validated is False:
            return -1
        (x_arr, y_arr) = validated
        if name == 'euclidean':
            return round(np.sqrt(np.sum((x_arr - y_arr) ** 2)), 4)
        elif name == 'manhattan':
            return round(np.sum(np.abs(x_arr - y_arr)), 4)
        elif name == 'chebyshev':
            return round(np.max(np.abs(x_arr - y_arr)), 4)
        elif name == 'cosine':
            norm_x = np.linalg.norm(x_arr)
            norm_y = np.linalg.norm(y_arr)
            if norm_x == 0 or norm_y == 0:
                return -1
            dot_product = np.dot(x_arr, y_arr)
            cosine_similarity = dot_product / (norm_x * norm_y)
            return round(1 - cosine_similarity, 4)
        else:
            return -1
    return distance_func

def blackjack_outcome(player: list[int], dealer: list[int]) -> float:
    """Evaluate the outcome of a finished round of Blackjack.

    The function **must** follow the rules presented in the task description.

    Args:
        player: List of integers (1–10) representing the player's final hand. 1 is Ace.
        dealer: List of integers (1–10) representing the dealer's final hand.

    Returns:
        The player's reward as a float. Possible values are -1, 0, 1 or 1.5.
    """

    def calculate_score(hand: list[int]) -> int:
        """Calculates the score of a hand, considering usable aces."""
        score = sum(hand)
        num_aces = hand.count(1)
        if num_aces > 0 and score + 10 <= 21:
            score += 10
        return score if score <= 21 else float('inf')

    def is_natural_blackjack(hand: list[int]) -> bool:
        """Checks if a hand is a natural blackjack."""
        return len(hand) == 2 and (1 in hand and 10 in hand or (10 in hand and 1 in hand))
    player_score = calculate_score(player)
    dealer_score = calculate_score(dealer)
    player_bust = player_score == float('inf')
    dealer_bust = dealer_score == float('inf')
    player_natural = is_natural_blackjack(player)
    if player_bust:
        return -1.0
    if dealer_bust:
        return 1.0
    if player_score > dealer_score:
        if player_natural:
            return 1.5
        else:
            return 1.0
    elif player_score < dealer_score:
        return -1.0
    else:
        return 0.0

