def gbdt_regression(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, n_estimators: int=10, learning_rate: float=0.1, max_depth: int=3) -> list[float]:
    """Gradient Boosting Decision Tree (GBDT) regressor.

    Parameters
    ----------
    X_train : np.ndarray
        2-D array with shape (n_samples, n_features) containing the training
        features.
    y_train : np.ndarray
        1-D array of length n_samples containing the training targets.
    X_test : np.ndarray
        2-D array with shape (m_samples, n_features) containing the test
        features to predict.
    n_estimators : int, default=10
        Number of boosting iterations.
    learning_rate : float, default=0.1
        Shrinkage factor applied to each tree’s prediction.
    max_depth : int, default=3
        Maximum depth of every individual regression tree.

    Returns
    -------
    list[float]
        Predictions for every sample in *X_test*, rounded to 4 decimal places.
    """

    class Node:

        def __init__(self, feature_index=None, threshold=None, left=None, right=None, value=None):
            self.feature_index = feature_index
            self.threshold = threshold
            self.left = left
            self.right = right
            self.value = value

    class RegressionTree:

        def __init__(self, max_depth):
            self.max_depth = max_depth
            self.root = None

        def _mse(self, y):
            if len(y) == 0:
                return 0
            return np.mean((y - np.mean(y)) ** 2)

        def _find_best_split(self, X, y):
            best_mse = float('inf')
            best_feature_index = None
            best_threshold = None
            (n_samples, n_features) = X.shape
            for feature_index in range(n_features):
                thresholds = np.unique(X[:, feature_index])
                for threshold in thresholds:
                    left_indices = np.where(X[:, feature_index] <= threshold)[0]
                    right_indices = np.where(X[:, feature_index] > threshold)[0]
                    if len(left_indices) == 0 or len(right_indices) == 0:
                        continue
                    left_y = y[left_indices]
                    right_y = y[right_indices]
                    current_mse = (len(left_y) * self._mse(left_y) + len(right_y) * self._mse(right_y)) / n_samples
                    if current_mse < best_mse:
                        best_mse = current_mse
                        best_feature_index = feature_index
                        best_threshold = threshold
            return (best_feature_index, best_threshold)

        def _build_tree(self, X, y, depth):
            n_samples = len(y)
            if depth >= self.max_depth or n_samples == 0 or self._mse(y) == 0:
                return Node(value=np.mean(y))
            (feature_index, threshold) = self._find_best_split(X, y)
            if feature_index is None:
                return Node(value=np.mean(y))
            left_indices = np.where(X[:, feature_index] <= threshold)[0]
            right_indices = np.where(X[:, feature_index] > threshold)[0]
            left_subtree = self._build_tree(X[left_indices], y[left_indices], depth + 1)
            right_subtree = self._build_tree(X[right_indices], y[right_indices], depth + 1)
            return Node(feature_index=feature_index, threshold=threshold, left=left_subtree, right=right_subtree)

        def fit(self, X, y):
            self.root = self._build_tree(X, y, 0)

        def _predict_single(self, x, node):
            if node.value is not None:
                return node.value
            if x[node.feature_index] <= node.threshold:
                return self._predict_single(x, node.left)
            else:
                return self._predict_single(x, node.right)

        def predict(self, X):
            return np.array([self._predict_single(x, self.root) for x in X])
    initial_prediction = np.mean(y_train)
    y_pred_train = np.full_like(y_train, initial_prediction, dtype=float)
    y_pred_test = np.full(X_test.shape[0], initial_prediction, dtype=float)
    for _ in range(n_estimators):
        residuals = y_train - y_pred_train
        tree = RegressionTree(max_depth=max_depth)
        tree.fit(X_train, residuals)
        y_pred_train += learning_rate * tree.predict(X_train)
        y_pred_test += learning_rate * tree.predict(X_test)
    final_predictions = [round(pred, 4) for pred in y_pred_test]
    return final_predictions

