def bayesian_linear_regression_pred(X: np.ndarray, y: np.ndarray, X_new: np.ndarray, mu: float | np.ndarray=0.0, sigma: float=1.0, V: float | np.ndarray | None=None, fit_intercept: bool=True) -> list[float]:
    """Bayesian linear regression with known variance.

    The function must return the MAP predictive means for every row in
    `X_new`.  See the task description for full details.
    """
    X_train = X.copy()
    X_new_pred = X_new.copy()
    if fit_intercept:
        X_train = np.hstack([np.ones((X_train.shape[0], 1)), X_train])
        X_new_pred = np.hstack([np.ones((X_new_pred.shape[0], 1)), X_new_pred])
    M = X_train.shape[1]
    if isinstance(mu, (int, float)):
        mu = np.full(M, mu)
    elif isinstance(mu, np.ndarray) and mu.ndim == 0:
        mu = np.full(M, mu.item())
    elif isinstance(mu, np.ndarray) and mu.ndim == 1 and (mu.shape[0] != M):
        raise ValueError(f'mu must be a scalar or a vector of length {M}')
    if V is None:
        V_inv = np.eye(M)
    elif isinstance(V, (int, float)):
        V_inv = np.eye(M) / V
    elif isinstance(V, np.ndarray) and V.ndim == 0:
        V_inv = np.eye(M) / V.item()
    elif isinstance(V, np.ndarray) and V.ndim == 1:
        if V.shape[0] != M:
            raise ValueError(f'V (diagonal) must be a vector of length {M}')
        V_inv = np.diag(1.0 / V)
    elif isinstance(V, np.ndarray) and V.ndim == 2:
        if V.shape != (M, M):
            raise ValueError(f'V (matrix) must be a {M}x{M} matrix')
        V_inv = np.linalg.inv(V)
    else:
        raise ValueError('Invalid type for V')
    XTX = X_train.T @ X_train
    XTy = X_train.T @ y
    Sigma_p = np.linalg.inv(V_inv + XTX)
    mu_p = Sigma_p @ (V_inv @ mu + XTy)
    y_pred = X_new_pred @ mu_p
    return [round(float(val), 4) for val in y_pred]

