import numpy as np
def sigmoid(x, order: int=0):
    """Compute the logistic sigmoid or its derivatives.

    Args:
        x (float | int | list | np.ndarray): Input data. Can be a scalar, list, or NumPy array.
        order (int, optional): 0 = function value, 1 = first derivative,
            2 = second derivative. Defaults to 0.

    Returns:
        float | list: Result rounded to 4 decimals. Scalar input returns a float;
            vector/matrix input returns a Python list preserving the shape.
            If *order* is not 0, 1, or 2, the function returns -1.
    """
    if order not in [0, 1, 2]:
        return -1
    is_scalar = not isinstance(x, (list, np.ndarray))
    x_np = np.asarray(x, dtype=float)
    sigma_x = 1 / (1 + np.exp(-x_np))
    if order == 0:
        result = sigma_x
    elif order == 1:
        result = sigma_x * (1 - sigma_x)
    else:
        result = sigma_x * (1 - sigma_x) * (1 - 2 * sigma_x)
    rounded_result = np.round(result, 4)
    if is_scalar:
        return float(rounded_result)
    else:
        return rounded_result.tolist()

import itertools
def apriori_frequent_itemsets(transactions: list[list[int]], min_sup: float) -> list[tuple]:
    """Find frequent itemsets with the Apriori algorithm.

    Parameters
    ----------
    transactions : list[list[Hashable]]
        List of transactions; each transaction is itself a list of items.
    min_sup : float
        Minimum support threshold expressed as a fraction (>0 and ≤1).

    Returns
    -------
    list[tuple]
        Frequent itemsets ordered by length and then lexicographically.
    """
    num_transactions = len(transactions)
    min_count = int(min_sup * num_transactions)

    def count_support(candidates, transactions):
        counts = {}
        for transaction in transactions:
            for candidate in candidates:
                if all((item in transaction for item in candidate)):
                    counts[candidate] = counts.get(candidate, 0) + 1
        return counts
    item_counts = {}
    for transaction in transactions:
        for item in transaction:
            item_counts[item] = item_counts.get(item, 0) + 1
    frequent_itemsets = []
    current_frequent_itemsets = []
    for (item, count) in item_counts.items():
        if count >= min_count:
            current_frequent_itemsets.append((item,))
    current_frequent_itemsets.sort()
    frequent_itemsets.extend(current_frequent_itemsets)
    k = 2
    while current_frequent_itemsets:
        candidates = set()
        for i in range(len(current_frequent_itemsets)):
            for j in range(i + 1, len(current_frequent_itemsets)):
                itemset1 = set(current_frequent_itemsets[i])
                itemset2 = set(current_frequent_itemsets[j])
                union_set = itemset1.union(itemset2)
                if len(union_set) == k:
                    candidate = tuple(sorted(list(union_set)))
                    candidates.add(candidate)
        pruned_candidates = set()
        for candidate in candidates:
            is_valid = True
            for subset_items in itertools.combinations(candidate, k - 1):
                subset = tuple(sorted(subset_items))
                if subset not in current_frequent_itemsets:
                    is_valid = False
                    break
            if is_valid:
                pruned_candidates.add(candidate)
        candidate_counts = count_support(pruned_candidates, transactions)
        current_frequent_itemsets = []
        for (candidate, count) in candidate_counts.items():
            if count >= min_count:
                current_frequent_itemsets.append(candidate)
        current_frequent_itemsets.sort()
        frequent_itemsets.extend(current_frequent_itemsets)
        k += 1
    return frequent_itemsets

import numpy as np
def rbf_kernel(X: list[list[int | float]], Y: list[list[int | float]] | None=None, sigma: float | list[float] | None=None) -> list[list[float]]:
    """Compute the Radial Basis Function (RBF) kernel matrix.

    The function should follow the specifications given in the task
    description. It must return -1 on invalid input, otherwise a nested list
    containing the kernel matrix rounded to four decimal places.
    """
    X_np = np.array(X, dtype=float)
    if Y is None:
        Y_np = X_np
    else:
        Y_np = np.array(Y, dtype=float)
    (N, C_X) = X_np.shape
    (M, C_Y) = Y_np.shape
    if C_X != C_Y:
        return -1
    if sigma is None:
        sigma_np = np.sqrt(C_X / 2.0)
    elif isinstance(sigma, (int, float)):
        if sigma <= 0:
            return -1
        sigma_np = float(sigma)
    elif isinstance(sigma, list):
        if len(sigma) != C_X:
            return -1
        sigma_np = np.array(sigma, dtype=float)
        if np.any(sigma_np <= 0):
            return -1
    else:
        return -1
    if sigma_np.ndim == 0:
        sigma_reshaped = sigma_np
    else:
        sigma_reshaped = sigma_np.reshape(1, -1)
    X_sq = np.sum(X_np ** 2 / sigma_reshaped ** 2, axis=1, keepdims=True)
    Y_sq = np.sum(Y_np ** 2 / sigma_reshaped ** 2, axis=1, keepdims=True)
    XY = X_np @ Y_np.T / sigma_reshaped ** 2
    sq_dist_scaled = X_sq - 2 * XY + Y_sq.T
    kernel_matrix = np.exp(-0.5 * sq_dist_scaled)
    rounded_kernel_matrix = np.round(kernel_matrix, 4)
    return rounded_kernel_matrix.tolist()

