def build_fp_tree(transactions, min_sup):
    """Builds the FP-tree and header table."""
    item_counts = Counter((item for transaction in transactions for item in transaction))
    frequent_items = {item for (item, count) in item_counts.items() if count >= min_sup}
    if not frequent_items:
        return (None, None)
    header_table = defaultdict(lambda : None)
    root = FPTreeNode(None, 0, None)
    for transaction in transactions:
        ordered_transaction = sorted([item for item in transaction if item in frequent_items], key=lambda item: (-item_counts[item], item))
        if ordered_transaction:
            current_node = root
            for item in ordered_transaction:
                if item not in current_node.children:
                    new_node = FPTreeNode(item, 0, current_node)
                    current_node.children[item] = new_node
                    if header_table[item] is None:
                        header_table[item] = new_node
                    else:
                        temp_node = header_table[item]
                        while temp_node.next_node is not None:
                            temp_node = temp_node.next_node
                        temp_node.next_node = new_node
                current_node = current_node.children[item]
                current_node.count += 1
    return (root, header_table)
