import math
def c(n):
    """Correction factor for path length."""
    if n == 1:
        return 0
    return 2 * np.log(n - 1) + 0.5772156649 - 2 * (n - 1) / n
def path_length(x, tree, current_depth=0):
    """Computes the path length of a single observation in a tree."""
    if tree is None:
        return current_depth + c(1)
    if 'split_feature' not in tree:
        return current_depth + c(tree['size'])
    split_feature = tree['split_feature']
    split_threshold = tree['split_threshold']
    if x[split_feature] < split_threshold:
        return path_length(x, tree['left'], current_depth + 1)
    else:
        return path_length(x, tree['right'], current_depth + 1)
def build_tree(data, height_limit, current_depth=0):
    """Recursively builds an isolation tree."""
    (n_samples, n_features) = data.shape
    if current_depth >= height_limit or n_samples <= 1:
        return {'size': n_samples}
    split_feature = np.random.randint(n_features)
    min_val = np.min(data[:, split_feature])
    max_val = np.max(data[:, split_feature])
    if min_val == max_val:
        return {'size': n_samples}
    split_threshold = np.random.uniform(min_val, max_val)
    left_indices = data[:, split_feature] < split_threshold
    right_indices = ~left_indices
    left_data = data[left_indices]
    right_data = data[right_indices]
    if left_data.shape[0] == 0 or right_data.shape[0] == 0:
        return {'size': n_samples}
    left_tree = build_tree(left_data, height_limit, current_depth + 1)
    right_tree = build_tree(right_data, height_limit, current_depth + 1)
    return {'split_feature': split_feature, 'split_threshold': split_threshold, 'left': left_tree, 'right': right_tree}
def detect_outliers(data: np.ndarray, n_trees: int, sample_size: int, epsilon: float) -> list[int]:
    """Tiny Isolation Forest implementation.

    Args:
        data: 2-D NumPy array where each row is an observation and each column a feature.
        n_trees: Number of random isolation trees that will be grown.
        sample_size: Number of samples drawn (without replacement) to grow every tree.
        epsilon: Fraction (0 ≤ ε < 1) of observations that are considered outliers.

    Returns:
        A list with the zero-based indices of the detected outliers, sorted increasingly.
    """
    np.random.seed(42)
    (n_samples, n_features) = data.shape
    sample_size = min(sample_size, n_samples)
    height_limit = math.ceil(np.log2(sample_size))
    all_path_lengths = np.zeros((n_samples, n_trees))
    for i in range(n_trees):
        if sample_size < n_samples:
            sample_indices = np.random.choice(n_samples, size=sample_size, replace=False)
            sampled_data = data[sample_indices]
            tree = build_tree(sampled_data, height_limit)
            for (j, original_index) in enumerate(sample_indices):
                all_path_lengths[original_index, i] = path_length(data[original_index], tree)
        else:
            tree = build_tree(data, height_limit)
            for j in range(n_samples):
                all_path_lengths[j, i] = path_length(data[j], tree)
    mean_path_lengths = np.mean(all_path_lengths, axis=1)
    if n_samples > 1:
        phi = 2 * np.log(n_samples - 1) - 2 * (n_samples - 1) / n_samples
    else:
        phi = 0
    anomaly_scores = 2 ** (-(mean_path_lengths / phi)) if phi != 0 else np.zeros(n_samples)
    if epsilon == 0:
        return []
    num_outliers = int(epsilon * n_samples)
    if num_outliers == 0 and epsilon > 0:
        num_outliers = 1 if n_samples > 0 else 0
    if num_outliers == 0:
        return []
    outlier_indices = np.argsort(anomaly_scores)[-num_outliers:]
    return sorted(outlier_indices.tolist())
