import numpy as np
def glm_links():
    """Construct and return standard GLM link functions.

    Returns
    -------
    dict
        A three-entry dictionary (identity, log, logit) where each entry is a
        dictionary containing callables for the link, its inverse and its
        derivative with respect to μ. All functions must work with numpy
        scalars as well as 1-D/2-D numpy arrays via element-wise operations.
    """

    def identity_link(mu):
        return mu

    def identity_inv_link(eta):
        return eta

    def identity_derivative(mu):
        return np.ones_like(mu)

    def log_link(mu):
        return np.log(mu)

    def log_inv_link(eta):
        return np.exp(eta)

    def log_derivative(mu):
        return 1 / mu

    def logit_link(mu):
        return np.log(mu / (1 - mu))

    def logit_inv_link(eta):
        return 1 / (1 + np.exp(-eta))

    def logit_derivative(mu):
        return 1 / (mu * (1 - mu))
    links = {'identity': {'link': identity_link, 'inv_link': identity_inv_link, 'derivative': identity_derivative}, 'log': {'link': log_link, 'inv_link': log_inv_link, 'derivative': log_derivative}, 'logit': {'link': logit_link, 'inv_link': logit_inv_link, 'derivative': logit_derivative}}
    return links

import numpy as np
from collections import Counter
def isolation_tree_path_length(data: np.ndarray) -> list[int]:
    """Compute the depth of the leaf reached by every sample in a random isolation tree.

    The tree is built following the rules described in the task statement and the
    random seed must be set to 0 so that results are reproducible.

    Args:
        data: A 2-D NumPy array of shape (n_samples, n_features).

    Returns:
        A list of length *n_samples* where the *i-th* element is the number of
        edges from the root to the leaf that contains the *i-th* sample.
    """
    np.random.seed(0)

    def build_isolation_tree(indices):
        if len(indices) <= 2:
            return {'leaf': indices}
        (n_samples, n_features) = data.shape
        f = np.random.randint(n_features)
        down = np.min(data[indices, f])
        up = np.max(data[indices, f])
        v = np.random.uniform(down, up)
        left_indices = indices[data[indices, f] <= v]
        right_indices = indices[data[indices, f] > v]
        return {'feature': f, 'threshold': v, 'left': build_isolation_tree(left_indices), 'right': build_isolation_tree(right_indices)}

    def get_depth(node, sample):
        if 'leaf' in node:
            return 0
        if sample[node['feature']] <= node['threshold']:
            return 1 + get_depth(node['left'], sample)
        else:
            return 1 + get_depth(node['right'], sample)
    tree = build_isolation_tree(np.arange(data.shape[0]))
    depths = [get_depth(tree, data[i]) for i in range(data.shape[0])]
    return depths

import numpy as np
import math
from collections import defaultdict
def adaboost_1d_predict(x_train: list[float], y_train: list[int], x_test: list[float], epsilon: float=0.0) -> list[int]:
    n = len(x_train)
    m = len(x_test)
    sample_weights = np.ones(n) / n
    stumps = []
    alphas = []

    def calculate_error(y_true, y_pred, weights):
        return np.sum(weights * (y_true != y_pred))

    def calculate_weighted_error(y_true, y_pred, weights):
        return np.sum(weights * (y_true != y_pred))

    def update_sample_weights(weights, alpha, y_true, y_pred):
        return weights * np.exp(-alpha * y_true * y_pred)

    def normalize_weights(weights):
        return weights / np.sum(weights)
    while True:
        best_stump = None
        min_error = float('inf')
        for i in range(n):
            for j in range(i, n):
                threshold = (x_train[i] + x_train[j]) / 2
                for d in [0, 1]:
                    y_pred = np.ones(n)
                    if d == 0:
                        y_pred[x_train > threshold] = -1
                    else:
                        y_pred[x_train <= threshold] = -1
                    error = calculate_weighted_error(y_train, y_pred, sample_weights)
                    if error < min_error:
                        min_error = error
                        best_stump = (d, threshold)
        alpha = 0.5 * math.log((1 - min_error) / min_error)
        y_pred = np.ones(n)
        if best_stump[0] == 0:
            y_pred[x_train > best_stump[1]] = -1
        else:
            y_pred[x_train <= best_stump[1]] = -1
        sample_weights = update_sample_weights(sample_weights, alpha, y_train, y_pred)
        sample_weights = normalize_weights(sample_weights)
        stumps.append(best_stump)
        alphas.append(alpha)
        y_pred_train = np.zeros(n)
        for i in range(len(stumps)):
            (d, threshold) = stumps[i]
            if d == 0:
                y_pred_train[x_train > threshold] += alphas[i]
            else:
                y_pred_train[x_train <= threshold] += alphas[i]
        y_pred_train = np.sign(y_pred_train)
        training_error = np.mean(y_pred_train != y_train)
        if training_error <= epsilon:
            break
    y_pred_test = np.zeros(m)
    for i in range(len(stumps)):
        (d, threshold) = stumps[i]
        if d == 0:
            y_pred_test[x_test > threshold] += alphas[i]
        else:
            y_pred_test[x_test <= threshold] += alphas[i]
    y_pred_test = np.sign(y_pred_test)
    return y_pred_test.tolist()

import numpy as np
def nesterov_update(w, velocity, grad_func, learning_rate=0.001, momentum=0.9):
    if not velocity:
        velocity = [0.0] * len(w)
    else:
        velocity = np.array(velocity)
    lookahead_point = w - momentum * velocity
    clipped_gradient = np.clip(grad_func(lookahead_point), -1, 1)
    new_velocity = momentum * velocity + learning_rate * clipped_gradient
    new_weights = w - new_velocity
    return (np.round(new_weights, 4).tolist(), np.round(new_velocity, 4).tolist())

def check_data(a, b):
    """Validate and convert the two inputs so that both are numeric.

    The function must accept integers, floats, or numeric strings. Any string
    should be converted with `float()`; existing numeric values stay as they
    are. The pair `(a, b)` must then be returned.
    """
    a = float(a) if isinstance(a, str) else a
    b = float(b) if isinstance(b, str) else b
    return (a, b)
def validate_input(function):

    def wrapper(a, b):
        (a, b) = check_data(a, b)
        return function(a, b)
    return wrapper
@validate_input
def sum_numbers(a, b):
    """Return the arithmetic sum of *a* and *b* after validation."""
    result = a + b
    return int(result) if result.is_integer() else result

from typing import List, Optional
class Node:
    """Binary decision-tree node (provided).

    Non-leaf node:   rule = (feature_index, threshold)
    Leaf node:       rule = (None, prediction_value)
    """

    def __init__(self, left: Optional['Node'], right: Optional['Node'], rule: tuple):
        self.left = left
        self.right = right
        self.feature = rule[0]
        self.threshold = rule[1]
def predict_tree(root: Optional[Node], X: List[List[float]]) -> List[float]:
    """Predict using the decision tree on a batch of samples."""
    if root is None:
        return []

    def predict_sample(node, sample):
        while node.left is not None and node.right is not None:
            if sample[node.feature] <= node.threshold:
                node = node.left
            else:
                node = node.right
        return node.threshold
    predictions = []
    for sample in X:
        predictions.append(predict_sample(root, sample))
    return predictions

import numpy as np
def rmsle(actual: list[float], predicted: list[float]) -> float:
    if len(actual) != len(predicted) or len(actual) == 0:
        return -1
    if any((val < 0 for val in actual)) or any((val < 0 for val in predicted)):
        return -1
    n = len(actual)
    rmsle_sum = np.sum((np.log(np.array(predicted) + 1) - np.log(np.array(actual) + 1)) ** 2)
    rmsle = np.sqrt(rmsle_sum / n)
    return round(float(rmsle), 4)

import numpy as np
def is_stochastic(X: list[list[float]] | 'np.ndarray') -> bool:
    """Return True if *X* is a row-stochastic matrix, otherwise False.

    A matrix is row-stochastic when every element is a probability (0 ≤ p ≤ 1)
    and each row sums to 1 (within a small numerical tolerance).

    Args:
        X: Matrix given as a list of lists or a NumPy array.

    Returns:
        bool: True if *X* is row-stochastic, False otherwise.
    """
    if isinstance(X, list):
        X = np.array(X)
    if not isinstance(X, np.ndarray):
        return False
    if X.ndim != 2:
        return False
    if not np.all((X >= 0) & (X <= 1)):
        return False
    row_sums = np.sum(X, axis=1)
    return np.allclose(row_sums, 1, atol=1e-08)

