import numpy as np
def newton(X: np.ndarray, y: np.ndarray, epsilon: float=1e-06, max_iter: int=1000) -> list[list[float]]:
    w = np.array([[1], [1]])
    n = X.shape[0]
    for _ in range(max_iter):
        gradient = 2 / n * X.T.dot(X.dot(w) - y)
        hessian = 2 / n * X.T.dot(X)
        if np.linalg.norm(gradient) < epsilon:
            break
        try:
            hessian_inv = np.linalg.inv(hessian)
        except np.linalg.LinAlgError:
            hessian_inv = np.linalg.pinv(hessian)
        w = w - hessian_inv.dot(gradient)
    return w.round(4).tolist()

import numpy as np
def softplus(x, deriv: bool=False):

    def sigmoid(x):
        return 1 / (1 + np.exp(-x))
    if isinstance(x, (int, float)):
        x = np.array([x])
    if deriv:
        result = sigmoid(x)
    else:
        result = np.log1p(np.exp(x))
    return [round(float(val), 4) for val in result]

import numpy as np
from collections import Counter
def unsmoothed_ngram_log_prob(corpus: list[str], sequence: list[str], N: int) -> float:
    """Compute the unsmoothed MLE N-gram log-probability of *sequence*."""
    ngram_counts = Counter()
    n_minus_1gram_counts = Counter()
    for i in range(len(corpus) - N + 1):
        ngram = tuple(corpus[i:i + N])
        n_minus_1gram = tuple(corpus[i:i + N - 1])
        ngram_counts[ngram] += 1
        n_minus_1gram_counts[n_minus_1gram] += 1
    log_prob = 0.0
    for i in range(len(sequence) - N + 1):
        ngram = tuple(sequence[i:i + N])
        n_minus_1gram = tuple(sequence[i:i + N - 1])
        ngram_count = ngram_counts[ngram]
        n_minus_1gram_count = n_minus_1gram_counts[n_minus_1gram]
        if n_minus_1gram_count == 0:
            return float('-inf')
        prob = ngram_count / n_minus_1gram_count
        if prob == 0:
            return float('-inf')
        log_prob += np.log(prob)
    return round(log_prob, 4)

import numpy as np
def compute_impurity(y, criterion):
    """Compute a node's impurity.

    Args:
        y (numpy.ndarray): 1-D array with the target values.
        criterion (str): One of {"entropy", "gini", "mse"}.

    Returns:
        float: Impurity value rounded to 4 decimal places.
    """
    if len(y) == 0:
        return 0.0
    if criterion == 'entropy':
        (unique, counts) = np.unique(y, return_counts=True)
        probabilities = counts / counts.sum()
        entropy = -np.sum(probabilities * np.log2(probabilities + 1e-10))
        return round(entropy, 4)
    elif criterion == 'gini':
        (unique, counts) = np.unique(y, return_counts=True)
        probabilities = counts / counts.sum()
        gini = 1 - np.sum(probabilities ** 2)
        return round(gini, 4)
    elif criterion == 'mse':
        mse = np.mean((y - np.mean(y)) ** 2)
        return round(mse, 4)
    else:
        raise ValueError("Criterion must be one of {'entropy', 'gini', 'mse'}")

import numpy as np
def lasso_regression(X: list[float] | 'np.ndarray', y: list[float] | 'np.ndarray', degree: int, reg_factor: float, n_iterations: int=1000, tol: float=1e-06) -> list[float]:
    X = np.array(X)
    y = np.array(y)
    X_design = np.vander(X, degree + 1, increasing=True)
    w = np.zeros(degree + 1)
    for _ in range(n_iterations):
        w_prev = np.copy(w)
        w[0] = np.mean(y - np.dot(X_design[:, 1:], w[1:]))
        for j in range(1, degree + 1):
            x_j = X_design[:, j]
            rho = x_j.dot(y - X_design.dot(w) + w[j] * x_j)
            w[j] = np.sign(rho) * max(abs(rho) - reg_factor, 0) / x_j.dot(x_j)
        if np.max(np.abs(w - w_prev)) < tol:
            break
    return np.round(w, 4).tolist()

import numpy as np
def dft(frame: np.ndarray, positive_only: bool=True) -> list:
    N = len(frame)
    c = []
    for k in range(N):
        coeff = 0
        for n in range(N):
            coeff += frame[n] * np.exp(-2j * np.pi * k * n / N)
        c.append(coeff)
    if positive_only:
        return [complex(round(coeff.real, 4), round(coeff.imag, 4)) for coeff in c[:N // 2 + 1]]
    else:
        return [complex(round(coeff.real, 4), round(coeff.imag, 4)) for coeff in c]
