import numpy as np
def sigmoid(Z: np.ndarray) -> np.ndarray:
    return 1 / (1 + np.exp(-Z))
def relu(Z: np.ndarray) -> np.ndarray:
    return np.maximum(0, Z)
def L_model_forward(X: np.ndarray, parameters: dict[str, np.ndarray]) -> list[list[float]]:
    caches = []
    A = X
    L = len(parameters) // 2
    for l in range(1, L):
        W = parameters['W' + str(l)]
        b = parameters['b' + str(l)]
        Z = np.dot(W, A) + b
        A = relu(Z)
        caches.append((Z, A))
    WL = parameters['W' + str(L)]
    bL = parameters['b' + str(L)]
    ZL = np.dot(WL, A) + bL
    AL = sigmoid(ZL)
    caches.append((ZL, AL))
    return np.round(AL, 4).tolist()

import numpy as np
def relu_backward(dA: list[list[int | float]], activation_cache: list[list[int | float]]) -> list[list[int | float]]:
    dA_np = np.array(dA)
    activation_cache_np = np.array(activation_cache)
    if dA_np.shape != activation_cache_np.shape:
        return -1
    dZ = dA_np * (activation_cache_np > 0)
    return dZ.tolist()

import numpy as np
def drelu(Z):
    """Return the element-wise derivative of the ReLU activation.

    Parameters
    ----------
    Z : int | float | list | numpy.ndarray
        Input data that can be a scalar, list (any depth), or ndarray.

    Returns
    -------
    float | list
        Derivative of ReLU with the same shape layout as *Z* (float if *Z* is a scalar).
    """
    if isinstance(Z, (int, float)):
        return 1.0 if Z > 0 else 0.0
    elif isinstance(Z, list):
        return [drelu(z) for z in Z]
    elif isinstance(Z, np.ndarray):
        return np.vectorize(lambda z: 1.0 if z > 0 else 0.0)(Z).tolist()
    else:
        raise ValueError('Input type not supported')

import numpy as np
def relu_(Z):
    """Apply the element-wise Rectified Linear Unit (ReLU) activation.

    The function keeps the container type of the input:
    scalar → scalar, list → list, NumPy array → NumPy array.

    Args:
        Z (int | float | list | numpy.ndarray): Input data – scalar, 1-D or 2-D.

    Returns:
        Same type as *Z* with all negative values clipped to 0.
    """
    if isinstance(Z, np.ndarray):
        return np.maximum(0, Z)
    elif isinstance(Z, list):
        if all((isinstance(i, list) for i in Z)):
            return [relu_(np.array(row)).tolist() for row in Z]
        else:
            return np.maximum(0, np.array(Z)).tolist()
    elif isinstance(Z, (int, float)):
        return max(0, Z)
    else:
        raise TypeError('Input must be a scalar, list, or numpy.ndarray.')

