import math
def entropy(labels):
    (unique_labels, label_counts) = np.unique(labels, return_counts=True)
    probabilities = label_counts / len(labels)
    return -np.sum(probabilities * np.log2(probabilities))
def information_gain(y: np.ndarray, splits: list[np.ndarray]) -> float:
    parent_entropy = entropy(y)
    total_samples = len(y)
    split_entropy = 0
    for split in splits:
        split_weight = len(split) / total_samples
        split_entropy += split_weight * entropy(split)
    return round(parent_entropy - split_entropy, 4)
