import numpy as np
def gaussian_pdf(x, mean, sigma):
    if sigma <= 0:
        return -1
    if not isinstance(x, (list, tuple, np.ndarray)):
        x = [x]
    pdf_values = []
    for val in x:
        pdf = 1 / (np.sqrt(2 * np.pi) * sigma) * np.exp(-(val - mean) ** 2 / (2 * sigma ** 2))
        pdf_values.append(round(pdf, 5))
    return pdf_values

import numpy as np
TIME_STEPS = 20
def string_to_int(text: str, time_steps: int, vocabulary: dict[str, int]) -> list[int]:
    encoded_text = [vocabulary.get(char, 0) for char in text]
    if len(encoded_text) < time_steps:
        encoded_text += [0] * (time_steps - len(encoded_text))
    else:
        encoded_text = encoded_text[:time_steps]
    return encoded_text
def int_to_string(indices, inverse_vocab: dict[int, str]) -> str:
    return ''.join([inverse_vocab[idx] for idx in indices if idx != 0])
def run_example(model, input_vocabulary: dict[str, int], inv_output_vocabulary: dict[int, str], text: str) -> str:
    encoded_text = string_to_int(text, TIME_STEPS, input_vocabulary)
    encoded_batch = np.array([encoded_text])
    predictions = model.predict(encoded_batch)
    predicted_ids = np.argmax(predictions, axis=-1)[0]
    decoded_text = int_to_string(predicted_ids, inv_output_vocabulary)
    return decoded_text

