from collections import deque
def escape_fire_maze(grid: list[str]) -> int:
    n = len(grid)
    if grid[0][0] == 'F' or grid[n - 1][n - 1] == 'F':
        return -1
    directions = [(0, 1), (0, -1), (1, 0), (-1, 0)]

    def is_valid(x, y):
        return 0 <= x < n and 0 <= y < n

    def spread_fire(fire):
        new_fire = set()
        for (x, y) in fire:
            for (dx, dy) in directions:
                (nx, ny) = (x + dx, y + dy)
                if is_valid(nx, ny) and grid[nx][ny] == '.':
                    new_fire.add((nx, ny))
        return new_fire

    def bfs():
        queue = deque([(0, 0, 0)])
        visited = set()
        visited.add((0, 0))
        fire = set()
        for i in range(n):
            for j in range(n):
                if grid[i][j] == 'F':
                    fire.add((i, j))
        while queue:
            (x, y, minutes) = queue.popleft()
            if (x, y) == (n - 1, n - 1):
                return minutes
            fire = spread_fire(fire)
            if (n - 1, n - 1) in fire:
                return -1
            new_queue = deque()
            for (dx, dy) in directions:
                (nx, ny) = (x + dx, y + dy)
                if is_valid(nx, ny) and grid[nx][ny] == '.' and ((nx, ny) not in fire) and ((nx, ny) not in visited):
                    new_queue.append((nx, ny, minutes + 1))
                    visited.add((nx, ny))
            queue.extend(new_queue)
        return -1
    return bfs()

