from heapq import heappush, heappop
from typing import List, Tuple, Union
def find_shortest_path(grid: List[List[int]]) -> Union[int, List[Tuple[int, int]]]:

    def heuristic(p):
        return abs(p[0] - goal[0]) + abs(p[1] - goal[1])

    def get_neighbors(p):
        neighbors = []
        for (dr, dc) in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
            (new_r, new_c) = (p[0] + dr, p[1] + dc)
            if 0 <= new_r < len(grid) and 0 <= new_c < len(grid[0]) and (grid[new_r][new_c] == 0):
                neighbors.append((new_r, new_c))
        return neighbors
    start = (0, 0)
    goal = (len(grid) - 1, len(grid[0]) - 1)
    open_set = [(heuristic(start), 0, start, [start])]
    closed_set = set()
    while open_set:
        (_, cost, current, path) = heappop(open_set)
        if current == goal:
            return path
        if current in closed_set:
            continue
        closed_set.add(current)
        for neighbor in get_neighbors(current):
            new_cost = cost + 1
            new_path = path + [neighbor]
            heappush(open_set, (new_cost + heuristic(neighbor), new_cost, neighbor, new_path))
    return -1

