    if activation == 'sigmoid':
        A = sigmoid(X)
        grad = np.round(upstream_grad * sigmoid_derivative(A), 4)
    elif activation == 'tanh':
        A = tanh(X)
        grad = np.round(upstream_grad * tanh_derivative(A), 4)
    elif activation == 'relu':
