def softsign(z):
    """Compute the Softsign activation function.

    Softsign(z) = z / (1 + |z|)

    The function accepts either a scalar or a list/NumPy array. For a scalar it
    returns a float; for an array-like input it returns a list of floats, with
    every element rounded to 4 decimal places.
    """
    if isinstance(z, (int, float)):
        result = z / (1 + abs(z))
        return round(result, 4)
    elif isinstance(z, (list, np.ndarray)):
        if len(z) == 0:
            return []
        result = [round(x / (1 + abs(x)), 4) for x in z]
        return result
    else:
        raise TypeError('Input must be a scalar, list, or NumPy array.')

import numpy as np
def max_norm(p, m=2.0, axis=0):
    epsilon = 1e-12
    if axis is None:
        norm = np.linalg.norm(p) + epsilon
        return np.round(p * min(1, m / norm), 4).tolist()
    slices = np.moveaxis(p, axis, 0)
    norm = np.linalg.norm(slices, axis=0) + epsilon
    clipped_slices = slices * np.minimum(1, m / norm)
    return np.moveaxis(np.round(clipped_slices, 4), 0, axis).tolist()

import numpy as np
def pam_clustering(X: np.ndarray, k: int) -> list[int]:
    (n_samples, n_features) = X.shape
    medoids = X[:k]
    labels = np.argmin(np.linalg.norm(X[:, np.newaxis] - medoids, axis=2), axis=1)
    total_cost = np.sum(np.min(np.linalg.norm(X[:, np.newaxis] - medoids, axis=2), axis=1))
    while True:
        improved = False
        for i in range(k):
            for j in range(n_samples):
                if j not in np.arange(k):
                    new_medoids = np.copy(medoids)
                    new_medoids[i] = X[j]
                    new_labels = np.argmin(np.linalg.norm(X[:, np.newaxis] - new_medoids, axis=2), axis=1)
                    new_total_cost = np.sum(np.min(np.linalg.norm(X[:, np.newaxis] - new_medoids, axis=2), axis=1))
                    if new_total_cost < total_cost:
                        total_cost = new_total_cost
                        medoids = new_medoids
                        labels = new_labels
                        improved = True
        if not improved:
            break
    return labels.tolist()

