from typing import List
def score(hand: List[str]) -> int:
    """Return the Blackjack score of *hand* or 0 if the hand is bust.

    Args:
        hand: A list of card labels ('2'-'10', 'J', 'Q', 'K', 'A').

    Returns:
        An integer between 0 and 21 (inclusive).  If every possible value of
        the hand exceeds 21 the function returns 0.
    """

    def possible_scores(hand):
        score = 0
        num_aces = 0
        for card in hand:
            if card in ['2', '3', '4', '5', '6', '7', '8', '9', '10']:
                score += int(card)
            elif card in ['J', 'Q', 'K']:
                score += 10
            elif card == 'A':
                num_aces += 1
                score += 11
        while score > 21 and num_aces:
            score -= 10
            num_aces -= 1
        return score
    scores = [possible_scores(hand)]
    if scores[0] > 21:
        return 0
    return max(scores)

