import numpy as np
def train_decision_stump(X: np.ndarray, y: np.ndarray, sample_weights: np.ndarray) -> dict:
    (n_samples, n_features) = X.shape
    best_error = np.inf
    best_stump = {}
    for feature_idx in range(n_features):
        unique_thresholds = np.unique(X[:, feature_idx])
        for threshold in unique_thresholds:
            for polarity in [1, -1]:
                predictions = np.ones(n_samples)
                predictions[polarity * X[:, feature_idx] >= polarity * threshold] = -1
                error = np.sum(sample_weights[predictions != y])
                if error < best_error:
                    best_error = error
                    best_stump = {'feature_index': feature_idx, 'threshold': round(threshold, 4), 'polarity': polarity, 'weighted_error': round(error, 4)}
    return best_stump

import numpy as np
def oracle_payoff(context: np.ndarray, thetas: np.ndarray) -> tuple[float, int]:
    rewards = np.dot(context.T, thetas)
    best_arm = np.argmax(rewards)
    best_reward = round(rewards[best_arm], 4)
    return (best_reward, best_arm)

import math
from collections import Counter
def calculate_entropy(labels):
    n = len(labels)
    if n <= 1:
        return 0
    counts = Counter(labels)
    probs = [count / n for count in counts.values()]
    return -sum((p * math.log2(p) for p in probs))
def best_split(feature, target):
    unique_values = sorted(set(feature))
    if len(unique_values) == 1:
        return (None, 0.0)
    best_threshold = None
    best_gain = 0.0
    parent_entropy = calculate_entropy(target)
    for i in range(1, len(unique_values)):
        threshold = (unique_values[i - 1] + unique_values[i]) / 2
        left_indices = [idx for (idx, val) in enumerate(feature) if val < threshold]
        right_indices = [idx for (idx, val) in enumerate(feature) if val >= threshold]
        if len(left_indices) == 0 or len(right_indices) == 0:
            continue
        left_labels = [target[idx] for idx in left_indices]
        right_labels = [target[idx] for idx in right_indices]
        left_entropy = calculate_entropy(left_labels)
        right_entropy = calculate_entropy(right_labels)
        information_gain = parent_entropy - len(left_indices) / len(target) * left_entropy - len(right_indices) / len(target) * right_entropy
        if information_gain > best_gain:
            best_gain = information_gain
            best_threshold = threshold
    return (round(best_threshold, 4), round(best_gain, 4))

import numpy as np
class L1Regularization:

    def __init__(self, alpha: float):
        """Initialize the L1Regularization with a given alpha.

        Args:
            alpha: Non-negative float controlling the strength of the regularisation.
        """
        if alpha < 0:
            raise ValueError('Alpha must be a non-negative float.')
        self.alpha = alpha

    def __call__(self, w: np.ndarray) -> float:
        """Calculate the L1 penalty value.

        Args:
            w: A NumPy array representing the weight vector.

        Returns:
            The L1 penalty value as a float.
        """
        if self.alpha == 0:
            return 0.0
        return self.alpha * np.sum(np.abs(w))

    def grad(self, w: np.ndarray) -> np.ndarray:
        """Calculate the gradient of the L1 penalty with respect to w.

        Args:
            w: A NumPy array representing the weight vector.

        Returns:
            The gradient as a NumPy array with the same shape as w.
        """
        if self.alpha == 0:
            return np.zeros_like(w)
        return self.alpha * np.sign(w)

import numpy as np
def average_pool2d(X: np.ndarray, pool_shape: tuple[int, int], stride: tuple[int, int], accum_grad: np.ndarray) -> tuple[list, list]:
    """Performs forward and backward passes of a 2-D average-pooling layer.

    The function must return a tuple (pooled_output, grad_input), both rounded
    to the nearest 4-th decimal and converted to regular Python lists using
    `tolist()`.
    """
    (N, C, H, W) = X.shape
    (p_h, p_w) = pool_shape
    (s_h, s_w) = stride
    pooled_output = np.zeros((N, C, (H - p_h) // s_h + 1, (W - p_w) // s_w + 1))
    for i in range(0, H - p_h + 1, s_h):
        for j in range(0, W - p_w + 1, s_w):
            pooled_output[:, :, i // s_h, j // s_w] = np.mean(X[:, :, i:i + p_h, j:j + p_w], axis=(2, 3))
    grad_input = np.zeros_like(X)
    for i in range(0, H - p_h + 1, s_h):
        for j in range(0, W - p_w + 1, s_w):
            grad_input[:, :, i:i + p_h, j:j + p_w] += np.expand_dims(np.expand_dims(accum_grad[:, :, i // s_h, j // s_w] / (p_h * p_w), axis=2), axis=3)
    return (np.round(pooled_output, decimals=4).tolist(), np.round(grad_input, decimals=4).tolist())

import numpy as np
def squared_error(actual, predicted):
    """Calculate the squared error or mean squared error.

    If *actual* and *predicted* are scalars, return the squared difference.
    If they are one-dimensional iterables (same length), return the mean squared
    error.  Return -1 for length mismatches or empty iterables.

    All results must be rounded to 4 decimal places.
    """
    if isinstance(actual, (int, float)) and isinstance(predicted, (int, float)):
        return round((actual - predicted) ** 2, 4)
    elif isinstance(actual, (list, tuple, np.ndarray)) and isinstance(predicted, (list, tuple, np.ndarray)):
        if len(actual) != len(predicted) or len(actual) == 0:
            return -1
        else:
            squared_errors = [(a - p) ** 2 for (a, p) in zip(actual, predicted)]
            mse = np.mean(squared_errors)
            return round(mse, 4)
    else:
        return -1

import numpy as np
from dataclasses import dataclass
@dataclass
class Leaf:
    """A terminal node that stores a prediction value."""
    value: object
@dataclass
class Node:
    """An internal decision-tree node.

    Attributes:
        feature (int):   Index of the feature to test.
        threshold (float): Threshold that splits the data.
        left (Node | Leaf):  Sub-tree for samples with feature value < threshold.
        right (Node | Leaf): Sub-tree for samples with feature value ≥ threshold.
    """
    feature: int
    threshold: float
    left: object
    right: object
def compare_trees(tree_a, tree_b):
    """Recursively checks whether *tree_a* and *tree_b* are equivalent.

    Args:
        tree_a: Root node of the first decision tree (Node or Leaf).
        tree_b: Root node of the second decision tree (Node or Leaf).

    Returns:
        True if the two trees are equivalent, False otherwise.
    """
    if isinstance(tree_a, Leaf) and isinstance(tree_b, Leaf):
        return np.allclose(tree_a.value, tree_b.value)
    if isinstance(tree_a, Node) and isinstance(tree_b, Node):
        if tree_a.feature != tree_b.feature:
            return False
        if not np.isclose(tree_a.threshold, tree_b.threshold, atol=1e-08):
            return False
        return compare_trees(tree_a.left, tree_b.left) and compare_trees(tree_a.right, tree_b.right)
    return False

from typing import Any, Dict, List, Tuple
def to_networkx(G: Dict[str, Any]) -> Dict[Any, List[Tuple[Any, float]]]:
    adj = {}
    for vertex_label in G['_V2I']:
        adj[vertex_label] = []
    for edge in G['_G']:
        (source_label, target_label, weight) = edge
        adj[source_label].append((target_label, weight))
        if not G['is_directed']:
            adj[target_label].append((source_label, weight))
    for vertex_label in adj:
        adj[vertex_label] = sorted(adj[vertex_label], key=lambda x: x[0])
    return adj

import heapq
from typing import List, Union
Number = Union[int, float]
def top_k_elements(sequence: List[Number], k: int, order: str='largest') -> List[Number]:
    if k <= 0 or not sequence:
        return []
    if k >= len(sequence):
        if order == 'largest':
            return sorted(sequence, reverse=True)
        else:
            return sorted(sequence)
    heap = []
    for num in sequence:
        if len(heap) < k:
            heapq.heappush(heap, num)
        elif order == 'largest':
            if num > heap[0]:
                heapq.heappop(heap)
                heapq.heappush(heap, num)
        elif num < heap[0]:
            heapq.heappop(heap)
            heapq.heappush(heap, num)
    if order == 'largest':
        return sorted(heap, reverse=True)
    else:
        return sorted(heap)
