def digamma(x):
    if x < 6:
        return digamma(x + 1) - 1 / x
    else:
        return math.log(x) - 1 / (2 * x) - 1 / (12 * x ** 2) + 1 / (120 * x ** 4) - 1 / (252 * x ** 6)
def expected_log_dirichlet(gamma, d, t):
    gamma_dt = gamma[d][t]
    sum_gamma_d = sum(gamma[d])
    expected_log = digamma(gamma_dt) - digamma(sum_gamma_d)
    return round(expected_log, 4)

import numpy as np
def get_loss(name: str):

    def mae(y_true, y_pred):
        return round(np.mean(np.abs(np.array(y_true) - np.array(y_pred))), 4)

    def mse(y_true, y_pred):
        return round(np.mean((np.array(y_true) - np.array(y_pred)) ** 2), 4)

    def ce(y_true, y_pred):
        y_pred = np.clip(y_pred, 1e-15, 1 - 1e-15)
        return round(-np.mean(y_true * np.log(y_pred) + (1 - y_true) * np.log(1 - y_pred)), 4)
    loss_functions = {'mae': mae, 'mean_absolute_error': mae, 'mse': mse, 'mean_squared_error': mse, 'cross_entropy': ce, 'log_loss': ce, 'ce': ce}
    canonical_name = name.lower().replace('_', '')
    if canonical_name in loss_functions:
        return loss_functions[canonical_name]
    else:
        raise ValueError('Invalid loss function.')
