def oob_mse(y_true, predictions, oob_mask):
    if not np.any(oob_mask):
        return -1
    oob_errors = []
    for i in range(len(y_true)):
        oob_predictions = []
        for j in range(len(predictions[i])):
            if oob_mask[i][j]:
                oob_predictions.append(predictions[i][j])
        if len(oob_predictions) > 0:
            oob_mean = np.mean(oob_predictions)
            oob_errors.append((y_true[i] - oob_mean) ** 2)
    oob_mse = np.mean(oob_errors)
    return round(oob_mse, 4)

from collections import Counter
def build_fp_tree(transactions: list[list[str]], min_support: int) -> dict:
    item_support = Counter((item for transaction in transactions for item in set(transaction)))
    frequent_items = {item: support for (item, support) in item_support.items() if support >= min_support}
    global_ordering = sorted(frequent_items.keys(), key=lambda x: (-frequent_items[x], x))
    root = {'support': 0, 'children': {}}
    for transaction in transactions:
        ordered_transaction = [item for item in transaction if item in global_ordering]
        current_node = root
        current_node['support'] += 1
        for item in ordered_transaction:
            if item not in current_node['children']:
                current_node['children'][item] = {'support': 0, 'children': {}}
            current_node = current_node['children'][item]
            current_node['support'] += 1
    return root
