from collections import Counter
class Node:

    def __init__(self, token, freq):
        self.token = token
        self.freq = freq
        self.left = None
        self.right = None

    def __lt__(self, other):
        if self.freq == other.freq:
            return self.token < other.token
        return self.freq < other.freq
def build_huffman_tree(tokens):
    frequency = Counter(tokens)
    heap = [Node(token, freq) for (token, freq) in frequency.items()]
    heapq.heapify(heap)
    while len(heap) > 1:
        left = heapq.heappop(heap)
        right = heapq.heappop(heap)
        parent = Node('', left.freq + right.freq)
        parent.left = left
        parent.right = right
        heapq.heappush(heap, parent)
    return heap[0]
def huffman_encode(tokens: list[str]) -> dict[str, str]:
    if not tokens:
        return {}
    if len(set(tokens)) == 1:
        return {tokens[0]: '0'}
    root = build_huffman_tree(tokens)
    codes = {}
    generate_huffman_codes(root, '', codes)
    return codes

