def wgan_gp_loss(Y_fake: np.ndarray, module: str, Y_real: np.ndarray | None=None, gradInterp: np.ndarray | None=None, lambda_: float=10.0) -> float:
    if module == 'C':
        if Y_real is None or gradInterp is None:
            raise ValueError('Y_real and gradInterp must be provided for critic loss.')
        loss_critic = np.mean(Y_fake) - np.mean(Y_real) + lambda_ * np.mean((np.linalg.norm(gradInterp, axis=1) - 1) ** 2)
        return round(loss_critic, 4)
    elif module == 'G':
        loss_generator = -np.mean(Y_fake)
        return round(loss_generator, 4)
    else:
        raise ValueError("Invalid module. Use 'C' for critic loss or 'G' for generator loss.")
