import numpy as np
def dct(frame: np.ndarray | list[float], orthonormal: bool=True) -> list[float]:
    N = len(frame)
    coefficients = []
    for k in range(N):
        ck = 0
        for n in range(N):
            ck += frame[n] * np.cos(np.pi * k * (2 * n + 1) / (2 * N))
        if orthonormal:
            if k == 0:
                ck *= 2 * np.sqrt(1 / (4 * N))
            else:
                ck *= 2 * np.sqrt(1 / (2 * N))
        coefficients.append(round(ck, 4))
    return coefficients
