    def __init__(self, gamma: float=1.0):
        super().__init__(gamma=gamma)
def parse_kernel_string(kernel_string: str) -> Dict[str, Any]:
    """Parse a kernel string and return the kernel type and parameters."""
    kernel_string = kernel_string.strip().lower()
    match = re.match('(\\w+)(\\((.*)\\))?', kernel_string)
    if not match:
        raise NotImplementedError(f'Unknown kernel specification: {kernel_string}')
    kernel_type = match.group(1)
    params = match.group(3)
    param_dict = {}
    if params:
        for param in params.split(','):
            (key, value) = param.split('=')
            param_dict[key.strip()] = eval(value.strip())
    return (kernel_type, param_dict)
def initialize_kernel(param: Optional[Any]=None) -> KernelBase:
    """Create and return a kernel object from various representations.

    Args:
        param: None (default LinearKernel), an existing KernelBase instance, a
            string description such as 'RBF(gamma=0.5)', or a dictionary with a
            'hyperparameters' entry as produced by KernelBase.summary().

    Returns:
        KernelBase: the newly created or forwarded kernel instance.
    """
    if param is None:
        return LinearKernel()
    if isinstance(param, KernelBase):
        return param
    if isinstance(param, str):
        (kernel_type, params) = parse_kernel_string(param)
        if kernel_type == 'linear':
            return LinearKernel()
        elif kernel_type == 'polynomial':
            return PolynomialKernel(**params)
        elif kernel_type == 'rbf':
            return RBFKernel(**params)
        else:
            raise NotImplementedError(f'Unknown kernel type: {kernel_type}')
    if isinstance(param, dict):
        if 'hyperparameters' not in param:
            raise ValueError("The dictionary must contain a 'hyperparameters' key.")
        hyperparams = param['hyperparameters']
        kernel_id = hyperparams.get('id')
        if kernel_id == 'LinearKernel':
            return LinearKernel()
        elif kernel_id == 'PolynomialKernel':
            return PolynomialKernel(**{k: v for (k, v) in hyperparams.items() if k != 'id'})
        elif kernel_id == 'RBFKernel':
            return RBFKernel(**{k: v for (k, v) in hyperparams.items() if k != 'id'})
        else:
            raise NotImplementedError(f'Unknown kernel id: {kernel_id}')
    raise ValueError('Invalid parameter type for kernel initialization.')

import numpy as np
def gradient_boosting_predict(updates: list[np.ndarray], learning_rate: float, regression: bool) -> np.ndarray:
    if not updates:
        raise ValueError('Updates list is empty')
    running_pred = np.zeros_like(updates[0])
    for u in updates:
        running_pred -= learning_rate * u
    if regression:
        return np.round(running_pred, 4)
    else:
        exp_pred = np.exp(running_pred - np.max(running_pred, axis=1, keepdims=True))
        softmax_pred = exp_pred / np.sum(exp_pred, axis=1, keepdims=True)
        return np.argmax(softmax_pred, axis=1)

