import numpy as np
def bayesian_linear_regression_pred(X: np.ndarray, y: np.ndarray, X_new: np.ndarray, mu: float | np.ndarray=0.0, sigma: float=1.0, V: float | np.ndarray | None=None, fit_intercept: bool=True) -> list[float]:
    """Bayesian linear regression with known variance.

    The function must return the MAP predictive means for every row in
    `X_new`.  See the task description for full details.
    """
    if fit_intercept:
        X = np.hstack((np.ones((X.shape[0], 1)), X))
        X_new = np.hstack((np.ones((X_new.shape[0], 1)), X_new))
    (N, M) = X.shape
    if isinstance(mu, (int, float)):
        mu = np.full(M, mu)
    elif len(mu) != M:
        raise ValueError('Length of mu must match the number of features in X.')
    if V is None:
        V = np.eye(M)
    elif isinstance(V, (int, float)):
        V = V * np.eye(M)
    elif V.shape != (M, M):
        raise ValueError('V must be a scalar, a 1-D array, or a square matrix of shape (M, M).')
    V_inv = np.linalg.inv(V)
    XTX = X.T @ X
    Sigma_p = np.linalg.inv(V_inv + XTX)
    mu_p = Sigma_p @ (V_inv @ mu + X.T @ y)
    y_pred = X_new @ mu_p
    return list(np.round(y_pred, 4))

import numpy as np
def dbscan(data: np.ndarray, eps: float, min_samples: int) -> list[int]:

    def region_query(data, point_index, eps):
        neighbors = []
        for i in range(len(data)):
            if np.linalg.norm(data[point_index] - data[i]) <= eps:
                neighbors.append(i)
        return neighbors

    def expand_cluster(data, labels, point_index, cluster_id, eps, min_samples):
        seeds = region_query(data, point_index, eps)
        if len(seeds) < min_samples:
            labels[point_index] = -1
            return False
        else:
            labels[point_index] = cluster_id
            for seed_index in seeds:
                labels[seed_index] = cluster_id
            while len(seeds) > 0:
                current_point = seeds[0]
                results = region_query(data, current_point, eps)
                if len(results) >= min_samples:
                    for i in range(len(results)):
                        result_point = results[i]
                        if labels[result_point] == 0 or labels[result_point] == -1:
                            if labels[result_point] == 0:
                                seeds.append(result_point)
                            labels[result_point] = cluster_id
                seeds = seeds[1:]
            return True
    labels = np.zeros(len(data))
    cluster_id = 0
    for point_index in range(len(data)):
        if labels[point_index] == 0:
            if expand_cluster(data, labels, point_index, cluster_id + 1, eps, min_samples):
                cluster_id += 1
    return list(map(int, labels))
